  KEY `elx_poll_id` (`poll_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=2 ;





CREATE TABLE `elx_poll_menu` (
  `pollid` int(11) NOT NULL DEFAULT '0',
