INSERT INTO `elx_categories` VALUES(12, 0, 'Καθαριότητας', 'Καθαριότητας', 'trash.jpg', '4', 'left', '<p><span style="font-family: arial;">Περιλαμβάνονται όλες οι προκηρύξεις καθαριότητας του Μετοχικού Ταμείου Πολιτικών Υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 1, 29, 0, NULL, 'greek', 'kathariotetas');
INSERT INTO `elx_categories` VALUES(13, 0, 'Συντήρηση  Κτιριακών Εγκαταστάσεων', 'Συντήρηση  Κτιριακών Εγκαταστάσεων', 'tools.jpg', '4', 'left', '<p><span style="font-family: arial;">Περιλαμβάνονται οι προκηρύξεις συντήρησης των κτιριακών εγκαταστάσεων του Μετοχικού Ταμείου Πολιτικών Υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 2, 29, 0, NULL, 'greek', 'sunterese-ktiriakon-egkatastaseon');
INSERT INTO `elx_categories` VALUES(14, 0, 'Συντήρηση  Τεχνολογικού   Εξοπλισμού', 'Συντήρηση  Τεχνολογικού  Εξοπλισμού', 'pc.jpg', '4', 'left', '<p><span>Περιλαμβάνονται οι προκηρύξεις τεχνολογικού εξοπλισμού του Μετοχικού Ταμείου Πολιτικών Υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 3, 29, 0, NULL, 'greek', 'sunterese-tekhnikou-exoplismou');
INSERT INTO `elx_categories` VALUES(15, 0, 'Δελτία  Τύπου', 'Δελτία  Τύπου', 'deltia.jpg', '5', 'left', '<p><span style="font-family: arial;">Περιλαμβάνονται τα δελτία τύπου για το Μετοχικό Ταμείο Πολιτικών Υπαλλήλων.</span></p>', 1, 62, '2014-01-15 09:09:49', NULL, 1, 29, 0, NULL, 'greek', 'deltia-tupou');
INSERT INTO `elx_categories` VALUES(18, 0, 'Εγκύκλιοι', 'Εγκύκλιοι', NULL, '6', 'left', '<p><span style="font-family: arial;">Πύλη πληροφοριών για τις κρατήσεις, για τον κανονισμό μερίσματος, για το αναλογιστικό ισοδύναμο καθώς και για την διαδοχική ασφάλιση.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 1, 29, 0, NULL, 'greek', 'egkuklioi');
INSERT INTO `elx_categories` VALUES(16, 0, 'ΜΜΕ', 'ΜΜΕ', 'efimerida.jpg', '5', 'left', '<p><span style="font-family: arial;">Περιλαμβάνεται η ενημέρωση του Μετοχικού Ταμείου Πολτικών Υπαλλήλων προς τα ΜΜΕ.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 2, 29, 0, NULL, 'greek', 'mme');
INSERT INTO `elx_categories` VALUES(17, 0, 'Πρόσκληση  ενδιαφέροντος', 'Πρόσκληση  ενδιαφέροντος', 'endiaferon.jpg', '5', 'left', '<p><span style="font-family: arial;">Περιλαμβάνοται οι προσκλήσεις εκδήλωσης ενδιαφέροντος για το Μετοχικό Ταμείο Πολιτικών Υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 3, 29, 0, NULL, 'greek', 'prosklese-endiapherontos');
INSERT INTO `elx_categories` VALUES(19, 0, 'Μελέτες', 'Μελέτες', NULL, '6', 'left', '<p>Πύλη Πληροφοριών για τον Στρατηγικό Σχεδιασμό του Μετοχικού Ταμείου Πολιτικών Υπαλλήλων και τον Απολογισμό Προγραμμάτων Δράσης.</p>', 1, 0, '1979-12-19 00:00:00', NULL, 2, 29, 0, NULL, 'greek', 'meletes');
INSERT INTO `elx_categories` VALUES(20, 0, 'Συλλογικά  όργανα', 'Συλλογικά  όργανα', NULL, '6', 'left', '<p><span style="font-family: arial;">Πύλη πληροφοριών για τα πρακτικά των συλλογικών οργάνων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 3, 29, 0, NULL, 'greek', 'sullogika-organa');
INSERT INTO `elx_categories` VALUES(21, 0, 'Εσωτερική  λειτουργία', 'Εσωτερική  λειτουργία', NULL, '6', 'left', '<p><span style="font-family: arial;">Πύλη πληροφοριών για τους κανονισμούς εσωτερικής οργάνωσης και λειτουργίας του Μετοχικού Ταμείου Πολιτικών Υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 4, 29, 0, NULL, 'greek', 'esoterike-leitourgia');
INSERT INTO `elx_categories` VALUES(22, 0, 'Πλαίσιο αξιολόγησης', 'Πλαίσιο αξιολόγησης', NULL, '6', 'left', '<p><span style="font-family: arial;">Πύλη πληροφοριών για το πλαίσιο αξιολόγησης του Μετοχικού Ταμείου Πολιτικών Υπαλλήλων προς τον πολίτη και την κοινωνία.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 5, 29, 0, NULL, 'greek', 'plaisio-axiologeses');
INSERT INTO `elx_categories` VALUES(23, 0, 'Σχόλια', 'Σχόλια', NULL, '6', 'left', '<p><span style="font-family: arial;">Πύλη υποβολής σχολίων από τους πολίτες για την αξιολόγηση του Μετοχικού Ταμείου Πολιτικών Υπαλλήλων. </span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 6, 29, 0, NULL, 'greek', 'skholia');
INSERT INTO `elx_categories` VALUES(24, 0, 'Απαντητικά  Έγγραφα', 'Απαντητικά Έγγραφα', NULL, '6', 'left', '<p><span style="font-family: arial;">Πύλη πληροφοριών για τα απαντητικά έγγραφα του Μετοχικού Ταμείου Πολιτικών Υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 7, 29, 0, NULL, 'greek', 'apantetika-eggrapha');
INSERT INTO `elx_categories` VALUES(25, 0, 'Προϋπολογισμοί', 'Προϋπολογισμοί', NULL, '7', 'left', '<p><span style="font-family: arial;">Στην πύλη  αυτή περιλαμβάνονται οι προϋπολογισμοί για το Μετοχικό Ταμείο Πολιτικών Υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 1, 29, 0, NULL, 'greek', 'proupologismoi');
INSERT INTO `elx_categories` VALUES(26, 0, 'Ισολογισμοί', 'Ισολογισμοί', NULL, '7', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιλαμβάνονται οι ισολογισμοί για το Μετοχικό Ταμείο Πολιτικών Υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 2, 29, 0, NULL, 'greek', 'isologismoi');
INSERT INTO `elx_categories` VALUES(27, 0, 'Ετήσιες εκθέσεις', 'Ετήσιες εκθέσεις', NULL, '7', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιλαμβάνονται οι ετήσιες εκθέσεις για το Μετοχικό Ταμείο Πολιτικών Υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 3, 29, 0, NULL, 'greek', 'etesies-ektheseis');
INSERT INTO `elx_categories` VALUES(28, 0, 'Προγράμματα Δράσεων', 'Προγράμματα Δράσεων', NULL, '7', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιλαμβάνονται τα προγράμματα δράσεων για το Μετοχικό Ταμείο Πολιτικών Υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 4, 29, 0, NULL, 'greek', 'programmata-draseon');
INSERT INTO `elx_categories` VALUES(29, 0, 'Ιστορία  Ταμείου', 'Ιστορία  Ταμείου', NULL, '8', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιλαμβάνονται η ίδρυση καθώς και άλλα επίπλεον ιστορικά στοιχεία που συνθέτουν την ιστορία του μετοχικού ταμείου πολιτικών υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 1, 29, 0, NULL, 'greek', 'istoria-tameiou');
INSERT INTO `elx_categories` VALUES(30, 0, 'Ταυτότητα  ταμείου', 'Ταυτότητα  ταμείου', NULL, '8', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή παρέχονται πληροφορίες για την γενική λειτουργία και ειδικότερα για την παραγωγή των μερισμάτων του μετοχικού ταμείου πολιτικών υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 2, 29, 0, NULL, 'greek', 'tautoteta-tameiou');
INSERT INTO `elx_categories` VALUES(31, 0, 'Διευθύνσεις', 'Διευθύνσεις', NULL, '8', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή παρέχει πληροφορίες για την διοικητική δομή όπως διοικητικό συμβούλιο, διευθύνσεις (3), τμήματα (21) καθώς και άλλες δομές του μετοχικού ταμείου πολιτικών υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 3, 29, 0, NULL, 'greek', 'dieuthunseis');
INSERT INTO `elx_categories` VALUES(32, 0, 'Νομικό  Πρόσωπο', 'Νομικό  Πρόσωπο', NULL, '8', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή παρέχονται πληροφορίες για το νομικό καθεστώς που διέπει το μετοχικό ταμείο πολιτικών υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 4, 29, 0, NULL, 'greek', 'nomiko-prosopo');
INSERT INTO `elx_categories` VALUES(33, 0, 'Στοιχεία', 'Στοιχεία', NULL, '9', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιγράφονται τα στοιχεία των μετόχων που συμμετέχουν στο μετοχικό ταμείο πολιτικών υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 1, 29, 0, NULL, 'greek', 'stoikheia');
INSERT INTO `elx_categories` VALUES(34, 0, 'Χρηματικές διευκολύνσεις', 'Χρηματικές διευκολύνσεις', NULL, '9', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιλαμβάνονται οι χρηματικές διευκολύνσεις του μετοχικού ταμείου πολιτικών υπαλλήλων προς τους μετόχους και τους μερισματούχους (προεξόφληση μερίσματος, προεξόφληση σύνταξης του Γενικού Λογιστηρίου του Κράτους, δάνεια στους εν ενεργεία υπαλλήλους).</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 2, 29, 0, NULL, 'greek', 'khrematikes-dieukolunseis');
INSERT INTO `elx_categories` VALUES(35, 0, 'Προϋπηρεσία', 'Προϋπηρεσία', NULL, '9', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιγράφονται η προϋπηρεσία καθώς και τα δικαιολογητικά που απαιτούνται προκειμένου να αναγνωριστεί.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 3, 29, 0, NULL, 'greek', 'prouperesia');
INSERT INTO `elx_categories` VALUES(36, 0, 'Κανονισμός  Μερίσματος', 'Κανονισμός Μερίσματος', NULL, '9', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή παρέχονται πληροφορίες για το δικαίωμα απόκτησης μερίσματος, τα δικαιολογητικά που απαιτούνται, τον τρόπο υπολογισμού, την διάρκειά του καθώς και την διακοπή του.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 4, 29, 0, NULL, 'greek', 'kanonismos-merismatos');
INSERT INTO `elx_categories` VALUES(37, 0, 'Διαδοχική Ασφάλιση', 'Διαδοχική Ασφάλιση', NULL, '9', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει πληροφορίες σχετικά με την διαδοχική ασφάλιση στο συνταξιοδοτικό σύστημα των ευρωπαικών κοινοτήτων, ο τρόπος υπολογισμού του καθώς και το αντίστοιχο ελληνικό.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 5, 29, 0, NULL, 'greek', 'diadokhike-asphalise');
INSERT INTO `elx_categories` VALUES(38, 0, 'Μέρισμα  θανόντος', 'Μέρισμα θανόντος', NULL, '9', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει πληροφορίες για την μεταβίβαση μερίσματος στο μετοχικό ταμείο πολιτικών υπαλλήλων λόγω θανάτου του μετόχου. Πιο συγκεκριμένα για τις προϋποθέσεις, τα δικαιολογητικά και το ποσό μερίσματος σε μια οικογένεια θανόντος.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 6, 29, 0, NULL, 'greek', 'merisma-thanontos');
INSERT INTO `elx_categories` VALUES(39, 0, 'Επιστροφή καταβληθέντων ποσών', 'Επιστροφή καταβληθέντων ποσών', NULL, '9', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιλαμβάνονται πληροφορίες για τα δικαιολογητικά που πρέπει να υποβληθούν:<br /></span><ol>\r\n<li><span style="font-family: arial;">υπερ καταθέσεων ΜΤΠΥ σε μετόχους, υπηρεσίες και δημόσιο.</span></li>\r\n<li><span style="font-family: arial;">υπερ κρατήσεων ΜΤΠΥ σε μη μετόχους ή μετόχους με λάθος ποσό κράτησης εκ παραδρομής</span></li>\r\n<li><span style="font-family: arial;">υπερ αποδοχών αργίας </span></li>\r\n</ol></p>', 1, 0, '1979-12-19 00:00:00', NULL, 7, 29, 0, NULL, 'greek', 'epistrophe-katablethenton-poson');
INSERT INTO `elx_categories` VALUES(40, 0, 'Πληρωμές μερισμάτων', 'Πληρωμές μερισμάτων', NULL, '10', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιγράφονται ο τρόπος και το ποσό πληρωμής του μερίσματος τόσο για τους μερισματούχους εσωτερικού και εξωτερικού. Επιπλέον παρέχονται πληροφορίες σε αυτούς για πιθανή αλλαγή διεύθυνσης καθώς και πιθανού τραπεζικού λογαριασμού.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 1, 29, 0, NULL, 'greek', 'pleromes-merismaton');
INSERT INTO `elx_categories` VALUES(41, 0, 'Προεξόφληση μερίσματος', 'Προεξόφληση μερίσματος', NULL, '10', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιλαμβάνονται πληροφορίες για την προεξόφληση μερίσματος στους μερισματούχους.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 2, 29, 0, NULL, 'greek', 'proexophlese-merismatos');
INSERT INTO `elx_categories` VALUES(42, 0, 'Προεξόφληση σύνταξης', 'Προεξόφληση σύνταξης', NULL, '10', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιλαμβάνονται πληροφορίες για την προεξόφληση σύνταξης στους μερισματούχους.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 3, 29, 0, NULL, 'greek', 'proexophlese-suntaxes');
INSERT INTO `elx_categories` VALUES(43, 0, 'Αναθεώρηση  μερίσματος', 'Αναθεώρηση  μερίσματος', NULL, '10', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιέχει πληροφορίες για την αναθεώρηση μερισμάτων προς τους μερισματούχους.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 4, 29, 0, NULL, 'greek', 'anatheorese-merismatos');
INSERT INTO `elx_categories` VALUES(44, 0, 'Μεταβίβαση  μερίσματος', 'Μεταβίβαση  μερίσματος', NULL, '10', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει πληροφορίες για την μεταβίβαση μερίσματος σε περίπτωση θανάτου όπως τα δικαιολογητικά που απαιτούνται, το ποσό του μερίσματος καθώς και ο τρόπος έναρξης, διακοπής και διαγραφής ενός μέλους.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 5, 29, 0, NULL, 'greek', 'metabibase-merismatos');
INSERT INTO `elx_categories` VALUES(45, 0, 'Υποχρεώσεις μετόχων', 'Υποχρεώσεις μετόχων', NULL, '11', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει πληροφορίες για τις αποδοχές και τα επιδόματα επί των οποίων διενεργούνται οι κρατήσεις <strong>των μετόχων </strong>καθώς και οι κατηγορίες των υπαλλήλων που υπάγονται.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 1, 29, 0, NULL, 'greek', 'upokhreoseis-metokhon');
INSERT INTO `elx_categories` VALUES(46, 0, 'Υποχρεώσεις μη μετόχων', 'Υποχρεώσεις μη μετόχων', NULL, '11', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει πληροφορίες για τις αποδοχές και τα επιδόματα επί των οποίων διενεργούνται οι κρατήσεις των μη μετόχων καθώς και οι κατηγορίες των υπαλλήλων που υπάγονται.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 2, 29, 0, NULL, 'greek', 'upokhreoseis-me-metokhon');
INSERT INTO `elx_categories` VALUES(47, 0, 'Επί των δαπανών', 'Επί των δαπανών', NULL, '11', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει τις κρατήσεις και τον υπολογισμό επί των δαπανών υπερ του ΜΤΠΥ αναλυτικά.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 3, 29, 0, NULL, 'greek', 'epi-ton-dapanon');
INSERT INTO `elx_categories` VALUES(48, 0, 'Συναλλαγή  ΕΤΕ', 'Συναλλαγή  ΕΤΕ', NULL, '11', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει οδηγίες για την συναλλαγή 4300 της Εθνικής τράπεζας της Ελλάδας υπερ του ΜΤΠΥ. Παρέχεται η εγκύκλιος και το σχετικό έντυπο της συναλλαγής. </span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 4, 29, 0, NULL, 'greek', 'sunallage-ete');
INSERT INTO `elx_categories` VALUES(49, 0, 'Ελεγκτές', 'Ελεγκτές', NULL, '12', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή αναφέρει εκτενώς τις δραστηριότητες του τμήματος των ελεγκτών του ΜΤΠΥ.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 1, 29, 0, NULL, 'greek', 'elegktes');
INSERT INTO `elx_categories` VALUES(50, 0, 'Επιτόπιοι Έλεγχοι', 'Επιτόπιοι Έλεγχοι', NULL, '12', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει αναλυτικές πληροφορίες για τους ελέγχους που διενεργήθηκαν κατά το παρελθόν από τους ελεγκτές του ΜΤΠΥ.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 2, 29, 0, NULL, 'greek', 'epitopioi-elegkhoi');
INSERT INTO `elx_categories` VALUES(51, 0, 'Εγκύλιοι', 'Εγκύκλιοι', NULL, '12', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει κάποιες εγκυκλίους που έχουν εκδοθεί και αφορούν το τμήμα ελέγχων του ΜΤΠΥ.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 3, 29, 0, NULL, 'greek', 'egkulioi');
INSERT INTO `elx_categories` VALUES(52, 0, 'Εκπαίδευση', 'Εκπαίδευση', 'university.jpg', 'com_weblinks', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμους συνδέσμους για διάφορα εκπαιδευτικά ιδρύματα.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 3, 29, 0, NULL, 'greek', 'ekpaideuse');
INSERT INTO `elx_categories` VALUES(53, 0, 'Ευρωπαϊκοί φορείς', 'Ευρωπαϊκοί φορείς', 'european.jpg', 'com_weblinks', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμους συνδέσμους για την ευρωπαϊκή ένωση.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 4, 29, 0, NULL, 'greek', 'europaikoi-phoreis');
INSERT INTO `elx_categories` VALUES(54, 0, 'Συνδικαλιστικοί  φορείς', 'Συνδικαλιστικοί φορείς', 'syndikalism.jpg', 'com_weblinks', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμους συνδέσμους για τους συνδικαλιστικούς φορείς.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 5, 29, 0, NULL, 'greek', 'sundikalistikoi-phoreis');
