INSERT INTO `elx_categories` VALUES(58, 0, 'Διοικητικό  Συμβούλιο', 'Διοικητικό Συμβούλιο', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το διοικητικό συμβούλιο του μετοχικού ταμείου πολιτικών υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 2, 29, 0, NULL, 'greek', 'dioiketiko-sumboulio');
INSERT INTO `elx_categories` VALUES(59, 0, 'Νομικό συμβούλιο  κράτους', 'Νομικό συμβούλιο κράτους', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για τον πάρεδρο και δικηγόρων του νομικού συμβουλίου του κράτους.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 3, 29, 0, NULL, 'greek', 'nomiko-sumboulio-kratous');
INSERT INTO `elx_categories` VALUES(60, 0, 'Τμήμα Προσωπικού', 'Τμήμα Προσωπικού', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα προσωπικού, το οποίο ασχολείται με τα θέματα υπηρεσιακής κατάστασης προσωπικού.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 5, 29, 0, NULL, 'greek', 'tmema-prosopikou');
INSERT INTO `elx_categories` VALUES(61, 0, 'Τμήμα Οικονομικού - Λογιστηρίου', 'Τμήμα Οικονομικού - Λογιστηρίου', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα οικονομικού - λογιστηρίου, το οποίο ασχολείται με λογιστική παρακολούθηση, κατάρτιση των οικονομικών καταστάσεων (Ισολογισμού, Προϋπολογισμού κλπ. ).</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 6, 29, 0, NULL, 'greek', 'tmema-oikonomikou-logisteriou');
INSERT INTO `elx_categories` VALUES(62, 0, 'Τμήμα Γραμματείας', 'Τμήμα Γραμματείας', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα γραμματείας, το οποίο ασχολείται με τα θέματα της διακίνησης εισερχόμενης και εξερχόμενης αλληλογραφίας.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 7, 29, 0, NULL, 'greek', 'tmema-grammateias');
INSERT INTO `elx_categories` VALUES(63, 0, 'Τμήμα Τεχνικής Υπηρεσίας', 'Τμήμα Τεχνικής Υπηρεσίας', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Τεχνικής Υπηρεσίας, το οποίο ασχολείται με τα θέματα της τεχνικής υπόστηριξης της Ακίνητης Περιουσίας του Ταμείου.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 8, 29, 0, NULL, 'greek', 'tmema-tekhnikes-uperesias');
INSERT INTO `elx_categories` VALUES(64, 0, 'Τμήμα Ποιότητας, Αποδοτικότητας και Απλούστευσης', 'Τμήμα Ποιότητας, Αποδοτικότητας και Απλούστευσης', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Ποιότητας, Αποδοτικότητας και Απλούστευσης των Διαδικασιών, το οποίο ασχολείται με τα θέματα της εφαρμογής μέτρων για την απλούστευση των γραφειοκρατικών διαδικασιών, για την καλύτερη εξυπηρέτηση των συναλλασομένων. Καθορισμός στόχων μέτρησης παραγωγικότητας.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 9, 29, 0, NULL, 'greek', 'tmema-poiotetas-apodotikotetas-kai-aplousteuses');
INSERT INTO `elx_categories` VALUES(65, 0, 'Τμήμα Μηχανογράφησης', 'Τμήμα Μηχανογράφησης', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Μηχανογράφησης, το οποίο ασχολείται με τα θέματα της κάλυψης των μηχανογραφικών αναγκών του Ταμείου.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 10, 29, 0, NULL, 'greek', 'tmema-mekhanographeses');
INSERT INTO `elx_categories` VALUES(66, 0, 'Τμήμα Εξυπηρέτησης Ασφαλισμένων', 'Τμήμα Εξυπηρέτησης Ασφαλισμένων', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Εξυπηρέτησης Ασφαλισμένων, το οποίο ασχολείται με τα θέματα της εξυπηρέτησης των συναλλασομένων, παροχή κάθε είδους πληροφοριών τηλεφωνικά, μέσω Fax &amp; μέσω e‐mail.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 12, 29, 0, NULL, 'greek', 'tmema-exupereteses-asphalismenon');
INSERT INTO `elx_categories` VALUES(67, 0, 'Τμήμα Κανονισμού Μερίσματος', 'Τμήμα Κανονισμού Μερίσματος', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Κανονισμού Μερίσματος, το οποίο ασχολείται με τα θέματα της επεξεργασίας αιτήσεων κανονισμού μερίσματος των νέων  μερισματούχων και θανόντων στην υπηρεσία.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 13, 29, 0, NULL, 'greek', 'tmema-kanonismou-merismatos');
INSERT INTO `elx_categories` VALUES(68, 0, 'Τμήμα Μεταβολών Μερίσματος', 'Τμήμα Μεταβολών Μερίσματος', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Μεταβολών Μερίσματος, το οποίο ασχολείται με τα θέματα των αναθεωρήσεων, αποφάσεων μεταβιβάσεως &amp; διαγραφές μελών ορφανικής οικογένειας. </span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 14, 29, 0, NULL, 'greek', 'tmema-metabolon-merismatos');
INSERT INTO `elx_categories` VALUES(69, 0, 'Τμήμα Διαδοχικής Ασφάλισης', 'Τμήμα Διαδοχικής Ασφάλισης', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Διαδοχικής Ασφάλισης, το οποίο ασχολείται με τα θέματα της εφαρμογή διαδοχικής ασφάλισης.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 15, 29, 0, NULL, 'greek', 'tmema-diadokhikes-asphalises');
INSERT INTO `elx_categories` VALUES(70, 0, 'Τμήμα Αναγνώρισης Προϋπηρεσίας', 'Τμήμα Αναγνώρισης Προϋπηρεσίας', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Αναγνώρισης Προϋπηρεσίας, το οποίο ασχολείται με τα θέματα της αναγνώρισης προϋπηρεσίας.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 16, 29, 0, NULL, 'greek', 'tmema-anagnorises-prouperesias');
INSERT INTO `elx_categories` VALUES(71, 0, 'Τμήμα Χρηματικών Διευκολύνσεων', 'Τμήμα Χρηματικών Διευκολύνσεων', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Χρηματικών Διευκολύνσεων, το οποίο ασχολείται με τα θέματα της χορήγησης δανείων, των προεξοφλήσεων μερίσματος &amp; σύνταξης στους μετόχους &amp; στους μερισματούχους του Ταμείου. Δυστυχώς έχουν ανασταλεί όλες οι χορηγήσεις.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 17, 29, 0, NULL, 'greek', 'tmema-khrematikon-dieukolunseon');
INSERT INTO `elx_categories` VALUES(72, 0, 'Τμήμα Ελεγκτών', 'Τμήμα Ελεγκτών', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Ελεγκτών, το οποίο ασχολείται με τα θέματα της διενέργειας ελέγχων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 18, 29, 0, NULL, 'greek', 'tmema-elegkton');
INSERT INTO `elx_categories` VALUES(73, 0, 'Τμήμα Διαχείρισης Χαρτοφυλακίου - Περιουσίας', 'Τμήμα Διαχείρισης Χαρτοφυλακίου - Περιουσίας', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Διαχείρισης Χαρτοφυλακίου - Ακίνητης Περιουσίας, το οποίο ασχολείται με τα θέματα της διαχείρισης κινητής &amp; ακίνητης περιουσίας του Ταμείου.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 20, 29, 0, NULL, 'greek', 'tmema-diakheirises-khartophulakiou-periousias');
INSERT INTO `elx_categories` VALUES(74, 0, 'Τμήμα Παρακολούθησης Καταθέσεων Μετόχων', 'Τμήμα Παρακολούθησης Καταθέσεων Μετόχων', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Παρακολούθησης Καταθέσεων Μετόχων, το οποίο ασχολείται με τα θέματα της παρακολούθησης και του ελέγχου της ορθότητας απόδοσης των καταθέσεων των μετόχων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 21, 29, 0, NULL, 'greek', 'tmema-parakoloutheses-katatheseon-metokhon');
INSERT INTO `elx_categories` VALUES(75, 0, 'Τμήμα Παρακολούθησης Πόρων', 'Τμήμα Παρακολούθησης Πόρων', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Παρακολούθησης Πόρων, το οποίο ασχολείται με τα θέματα της παρακολούθησης και ελέγχου της ορθότητας απόδοσηςτων πόρων του Ταμείου.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 22, 29, 0, NULL, 'greek', 'tmema-parakoloutheses-poron');
INSERT INTO `elx_categories` VALUES(76, 0, 'Τμήμα Παρακολούθησης Χρηματικών Διευκολύνσεων', 'Τμήμα Παρακολούθησης Χρηματικών Διευκολύνσεων', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Παρακολούθησης Χρηματικών Διευκολύνσεων, το οποίο ασχολείται με τα θέματα της παρακολούθησης της εμπρόθεσμης καταβολής των χρηματικών διευκολύνσεων (τοκοχρεολυτικών δόσεων δανείων, προεξοφλήσεων μερίσματος &amp; σύνταξης).</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 23, 29, 0, NULL, 'greek', 'tmema-parakoloutheses-khrematikon-dieukolunseon');
INSERT INTO `elx_categories` VALUES(77, 0, 'Τμήμα Ταμείου', 'Τμήμα Ταμείου', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Ταμείου, το οποίο ασχολείται με τα θέματα της είσπραξης και πληρωμής χρηματικών ποσών του Ταμείου.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 24, 29, 0, NULL, 'greek', 'tmema-tameiou');
INSERT INTO `elx_categories` VALUES(78, 0, 'Τμήμα Καταβολής Μερισμάτων & Ηλεκτρ. Πληρωμών', 'Τμήμα Καταβολής Μερισμάτων & Ηλεκτρ. Πληρωμών', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τμήμα Καταβολής Μερισμάτων και των Ηλεκτρονικών Πληρωμών, το οποίο ασχολείται με τα θέματα της Παρακολούθηση της καταβολής των μερισμάτων στους δικαιούχους.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 25, 29, 0, NULL, 'greek', 'tmema-kataboles-merismaton');
INSERT INTO `elx_categories` VALUES(79, 0, 'Σύλλογος Υπαλλήλων Μετοχικού Ταμείου', 'Σύλλογος Υπαλλήλων Μετοχικού Ταμείου', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για τον σύλλογο εργαζομέμων του Μετοχικού Ταμείου Πολιτικών Υπαλλήλων, ο οποίο ασχολείται με τα θέματα συνδικαλιστικού περιεχομένου των εργαζομένων του ΜΤΠΥ.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 26, 29, 0, NULL, 'greek', 'sullogos-upallelon-metokhikou-tameiou');
INSERT INTO `elx_categories` VALUES(80, 0, 'Διεύθυνση Διοικητικού', 'Διεύθυνση Διοικητικού', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για την Διεύθυνση Προσωπικού, η οποία ασχολείται με τα θέματα της διοικητικής υποστήριξης των Υπηρεσιών του Ταμείου (λογιστήριο, επιμελητεία, μηχανογράφηση, τεχνική υπηρεσία κλπ.)</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 4, 29, 0, NULL, 'greek', 'dieuthunse-dioiketikou');
INSERT INTO `elx_categories` VALUES(81, 0, 'Διεύθυνση Ασφαλισμένων', 'Διεύθυνση Ασφαλισμένων', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για την Διεύθυνση Ασφαλισμένων, η οποία ασχολείται με τα θέματα της εξυπηρέτησης των μελών του Ταμείου, μετόχων και μερισματούχων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 11, 29, 0, NULL, 'greek', 'dieuthunse-asphalismenon');
INSERT INTO `elx_categories` VALUES(82, 0, 'Διεύθυνση Διαχείρισης Διαθέσιμων Χαρτοφυλακίου', 'Διεύθυνση Διαχείρισης Διαθέσιμων Χαρτοφυλακίου', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για την Διεύθυνση Διαχείρισης Διαθέσιμων Χαρτοφυλακίου, η οποία ασχολείται με τα θέματα της αξιοποίησης της κινητής και ακίνητης περιουσίας του Ταμείου, καθώς και για τον έλεγχο και την παρακολούθηση των εισροών (κρατήσεις και πόροι), των εκροών (μερίσματα) και των απαιτήσεών του (χρηματικές διευκολύνσεις).</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 19, 29, 0, NULL, 'greek', 'dieuthunse-diakheirises-diathesimon-khartophulakiou');
INSERT INTO `elx_categories` VALUES(83, 0, 'Μετοχικό  Ταμείο', 'Μετοχικό  Ταμείο', NULL, 'com_contact_details', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει χρήσιμες πληροφορίες επαφής για το τηλεφωνικό κέντρο του μετοχικού ταμείου πολιτικών υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 27, 29, 0, NULL, 'greek', 'metokhiko-tameio');
INSERT INTO `elx_categories` VALUES(84, 0, 'ΔΕΚΟ', 'ΔΕΚΟ', NULL, '13', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει τους οδηγούς κρατήσεων υπερ του ΜΤΠΥ για τις ΔΕΚΟ.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 5, 29, 0, NULL, 'greek', 'deko');
INSERT INTO `elx_categories` VALUES(85, 0, 'Υπάλληλοι', 'Υπάλληλοι', NULL, '13', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει τους οδηγούς κρατήσεων υπερ του ΜΤΠΥ για τους υπαλλήλους.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 6, 29, 0, NULL, 'greek', 'upalleloi');
INSERT INTO `elx_categories` VALUES(86, 0, 'Πανεπιστήμια', 'Πανεπιστήμια', NULL, '13', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει τους οδηγούς κρατήσεων υπερ του ΜΤΠΥ για τα πανεπιστήμια (AEI - TEI) και τους υπαλλήλους τους.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 7, 29, 0, NULL, 'greek', 'panepistemia');
INSERT INTO `elx_categories` VALUES(87, 0, 'Τοπική Αυτοδιοίκηση', 'Τοπική Αυτοδιοίκηση', NULL, '13', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει τους οδηγούς κρατήσεων υπερ του ΜΤΠΥ για τις υπηρεσίες Τοπικής Αυτοδιοίκησης και των υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 8, 29, 0, NULL, 'greek', 'topike-autodioikese');
INSERT INTO `elx_categories` VALUES(88, 0, 'Οδοιπορικά', 'Οδοιπορικά', NULL, '13', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει τους οδηγούς κρατήσεων υπερ του ΜΤΠΥ για τα οδοιπορικά.</span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 9, 29, 0, NULL, 'greek', 'odoiporika');
INSERT INTO `elx_categories` VALUES(89, 0, 'Μέλη του ΜΤΠΥ', 'Μέλη του ΜΤΠΥ', NULL, '14', 'left', '<p><span style="font-family: arial;">Στα πλαίσια της πιλοτικής εφαρμογής του Κοινού Πλαισίου Αξιολόγησης (ΚΠΑ) στο Μ.Τ.Π.Υ. (Σύστημα Διοίκησης Ολικής Ποιότητας), τα μέλη του ΜΤΠΥ οφείλουν να καταθέσουν τις απόψεις τους ηλεκτρονικά στις παρακάτω φόρμες.</span></p>\r\n<p></p>', 1, 0, '1979-12-19 00:00:00', NULL, 1, 29, 0, NULL, 'greek', 'mele-tou-mtpu');
INSERT INTO `elx_categories` VALUES(90, 0, 'Συναλλασσόμενοι', 'Συναλλασσόμενοι', NULL, '14', 'left', '<p><span style="font-family: arial;">Το κοινό οφείλει να αφιερώσει λίγο από τον ελεύθερο χρόνο του είτε να απαντήσει ηλεκτρονικά στο παρακάτω ερωτηματόλιο που αποσκοπεί στην βελτίωση και αναβάθμιση των υπηρεσιών του ΜΤΠΥ είτε να εκφράσει ηλεκτρονικά τα παράπονά του και τις υποδείξεις του για τα ''κακώς κείμενα'' του ΜΤΠΥ στην φόρμα παραπόνων.<br /><br />Σας ευχαριστούμε θερμά για το ενδιαφέρον σας και υποσχόμαστε ότι θα λάβουμε πρωτοβουλία για αυτά στο άμεσο μέλλον. </span></p>', 1, 0, '1979-12-19 00:00:00', NULL, 2, 29, 0, NULL, 'greek', 'sunallassomenoi');
