INSERT INTO `elx_content` VALUES(44, 'Σχεδιασμός 2006 και Δράση 2008', 'skhediasmos-2006-kai-drase-2008', '<p><span style="font-family: arial;">Μακροχρόνιος στρατηγικός σχεδιασμός ετών 2006 - 2007 και απολογισμός προγραμμάτων δράσης ετών 2008-2009 για το Μετοχικό Ταμείο Πολιτικών Υπαλλήλων.</span></p>', NULL, 1, 6, 0, 19, '2014-01-16 08:58:44', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-16 08:58:00', '2060-01-01 00:00:00', NULL, 'Δείτε την σχετική  μελέτη|/mtpy/efimerida/meletes/sxediasmos2006.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'Στρατηγικός σχεδιασμός 2006-2007\r\nΑπολογισμός προγραμμάτων δράσης 2008-2009 \r\nΜετοχικό Ταμείο Πολιτικών Υπαλλήλων', 'Στρατηγικός Σχεδιασμός 2006 και Δράση 2008', 29, 0, 'greek');
