INSERT INTO `elx_content` VALUES(62, 'Δράσεις 2012-2015', 'draseis-2012-2015', '<p><span style="font-family: arial;">Το πρόγραμμα δράσεων των οικονομικών ετών 2012-2015.</span></p>', NULL, 1, 7, 0, 28, '2014-01-20 10:29:41', 62, NULL, '2014-01-20 10:30:30', 62, 0, '1979-12-19 00:00:00', '2014-01-20 10:29:00', '2060-01-01 00:00:00', NULL, 'Δείτε το σχετικό πρόγραμμα:|/mtpy/ekonomika/draseis/drasi2011.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 2, 'πρόγραμμα δράσεων 2012-2015\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'πρόγραμμα δράσεων 2012-2015', 29, 2, 'greek');
INSERT INTO `elx_content` VALUES(63, 'Δράσεις 2010-2011', 'draseis-2010-2011', '<p><span style="font-family: arial;">Το πρόγραμμα δράσεων των οικονομικών ετών  2010-2011.</span></p>', NULL, 1, 7, 0, 28, '2014-01-20 10:31:32', 62, NULL, '2014-01-20 10:33:52', 62, 0, '1979-12-19 00:00:00', '2014-01-20 10:31:00', '2060-01-01 00:00:00', NULL, 'Δείτε το σχετικό πρόγραμμα:|/mtpy/ekonomika/draseis/drasi2010.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 1, 'πρόγραμμα δράσεων 2010-2011\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'πρόγραμμα δράσεων 2010-2011', 29, 2, 'greek');
INSERT INTO `elx_content` VALUES(64, 'Ίδρυση', 'idruse', '<p><span style="font-family: arial;">Tο Μετοχικό Ταμείο Πολιτικών Υπαλλήλων (Μ.Τ.Π.Υ.) ιδρύθηκε στις 12  Οκτωβρίου του 1867 με πρωτοβουλία του τότε προέδρου του Ελεγκτικού Συνεδρίου Δημ. Ν. Λεβίδη και από πενήντα ακόμη  κρατικούς κατά κύριο λόγο υπαλλήλους, οι οποίοι ίδρυσαν Ανώνυμη Εταιρεία με την επωνυμία «Μετοχικόν Ταμείον προς περίθαλψην των υπαλλήλων και των οικογενειών των», με κεφάλαια τις εκούσιες καταθέσεις τους.</span></p>\r\n<p></p>', '<p><span style="font-family: arial;">Τα πρώτα μερίσματα καταβλήθηκαν το 1874 και αντιστοιχούσαν στο σύνολο των καταθέσεων των μετόχων μέχρι και το 1882 .<br /></span><span style="font-family: arial;">Το 1887 τα καταβαλλόμενα μερίσματα αντιστοιχούσαν  στο 30-40% των καταθέσεων των μετόχων. <br /></span><span style="font-family: arial;">Το 1893,  με την πτώχευση της Ελλάδος επί Κυβερνήσεως Χαρ. Τρικούπη, και τη μείωση των τόκων από τα χρεόγραφα, μειωθήκαν ακόμη περισσότερο τα καταβαλλόμενα μερίσματα.<br /></span><span style="font-family: arial;">Το 1896 συντάσσεται το Μητρώο των μετόχων, στο οποίο καταχωρήθηκαν οι μέχρι τις 31 Δεκεμβρίου 1885 εγγραφέντες μέτοχοι του ταμείου. <br /></span><span style="font-family: arial;">Το 1905 ζητήθηκε από πολλούς μετόχους η διάλυση του Ταμείου επειδή το ποσό του μερίσματος μειώθηκε κάτω και από το μισό.<br /></span><span style="font-family: arial;">Έως και το 1917 συνεχίστηκε η μείωση των μερισμάτων λόγω των αθρόων απολύσεων, της κατάργησης της μονιμότητας των δημοσίων υπαλλήλων και της μη εγγραφής νέων μετόχων. </span></p>', 1, 8, 0, 29, '2014-01-21 09:57:41', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-21 09:57:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 6, 'ίδρυση \r\nιστορικά στοιχεία\r\nμετοχικού ταμείου πολιτικών υπαλλήλων', 'Ίδρυση μετοχικού ταμείου πολιτικών υπαλλήλων', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(65, 'Δημόσια  μορφή', 'demosia-morphe', '<p><span style="font-family: arial;">Σταθμός στην ιστορία του Ταμείου αποτελεί ο Ν.1636/1919 με τον οποίο το Ταμείο εισήλθε στο δημόσιο χώρο και η συμμετοχή σε αυτό  με το Ν.Δ. 12/2-1/3/1923  έγινε υποχρεωτική από την 1η Απριλίου του 1923   για όλους τους εν ενεργεία πολιτικούς υπαλλήλους με ελάχιστες εξαιρέσεις, ορίζεται δε ως ειδικό Ταμείο του Κράτους υπαγόμενο στην αρμοδιότητα του Υπουργείου των Οικονομικών.</span></p>\r\n<p></p>', '<p><span style="font-family: arial;">Με  το Νόμο 1500/1953 που καθιερώνεται ο θεσμός της 35ετιας λόγω των αθρόων παραιτήσεων, τα  συνολικά ποσά για τα καταβαλλόμενα μερίσματα υπερέβησαν το σύνολο των εισροών του Ταμείου και έτσι τα έτη 1965-1968  αναγκάστηκε να συνάψει άτοκο δάνειο με το Δημόσιο.</span></p>', 1, 8, 0, 29, '2014-01-21 10:01:10', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-21 10:01:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 5, 'Δημόσια μορφή\r\nμετοχικό ταμείο πολιτικών υπαλλήλων\r\nιστορικά στοιχεία', 'Δημόσια μορφή μετοχικού ταμείου πολιτικών υπαλλήλων', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(66, 'Ν.3336/05  2005', 'n333605-2005', '<p><span style="font-family: arial;"><span style="text-align: justify; background-color: #e5e9f1;">Το 2005 ψηφίζεται ο Ν.3336/05 με τον οποίο καθιερώνεται νέος τρόπος υπολογισμού του μερίσματος, καθώς και η αναπροσαρμογή των μερισμάτων.</span><br style="text-align: justify; background-color: #e5e9f1;" /><span style="text-align: justify; background-color: #e5e9f1;">Η εφαρμογή του νόμου αυτού είχε σα συνέπεια να απομακρύνει το Ταμείο από τη βασική καταστατική του διάταξη που είναι η «κατ’ αναλογία του ποσού των καταθέσεων ενός εκάστου παροχή μερίσματος».</span></span></p>', NULL, 1, 8, 0, 29, '2014-01-21 10:03:57', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-21 10:03:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 4, 'Ν.3336/05 του 2005  \r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Ν.3336/05 2005 μετοχικό ταμείο πολιτικών υπαλλήλων', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(67, 'Ν.4024   2011', 'n4024-2011', '<p><span style="font-family: arial;"><span style="text-align: justify; background-color: #e5e9f1;">Το 2011, με το Ν.4024 καθιερώνεται νέος τρόπος υπολογισμού του μερίσματος, προκειμένου να επαναφέρει το Ταμείο στη βασική καταστατική του διάταξη για τον τρόπο υπολογισμού του μερίσματος.</span><br style="text-align: justify; background-color: #e5e9f1;" /><span style="text-align: justify; background-color: #e5e9f1;">Ακόμη, καθιερώνεται η υπαγωγή του Ταμείου στην αρμοδιότητα του Υπουργείου Εργασίας και Κοινωνικής Ασφάλισης.</span><br style="text-align: justify; background-color: #e5e9f1;" /><span style="text-align: justify; background-color: #e5e9f1;">Τέλος, με απόφαση του υπουργού Εργασίας και Κοινωνικής Ασφάλισης, ορίστηκε η πληρωμή των μερισμάτων κατά μήνα.</span></span></p>', NULL, 1, 8, 0, 29, '2014-01-21 10:05:35', 62, NULL, '2014-01-21 10:05:51', 62, 0, '1979-12-19 00:00:00', '2014-01-21 10:05:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 3, 'Ν.4024 του 2011 μετοχικό ταμείο πολιτικών υπαλλήλων', 'Ν.4024 του 2011 μετοχικό ταμείο πολιτικών υπαλλήλων', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(68, 'Ν.4052  2012', 'n4052-2012', '<p><span style="text-align: justify; background-color: #e5e9f1;"><span style="font-family: arial;">Το 2012 με τον Ν.4052 τροποποιείται εκ νέου ο τρόπος υπολογισμού του μερίσματος, ώστε αυτό να υπολογίζεται με δικαιότερο τρόπο και να είναι ανάλογο του χρόνου κατά τον οποίο διενεργήθηκαν κρατήσεις.</span></span></p>', NULL, 1, 8, 0, 29, '2014-01-21 10:07:38', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-21 10:07:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 2, 'Ν.4052 του 2012 \r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Ν.4052 του 2012', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(69, 'Γενική  Λειτουργία', 'genike-leitourgia', '<p><span style="font-family: arial;">Η γενική λειτουργία του συνοψίζεται στα εξής:</span></p>', '<p><ol>\r\n<li><span style="font-family: arial;">Προορισμός του Ταμείου είναι σύμφωνα με το Π.Δ. 422/81 «… η κατ΄ αναλογία του ποσού των καταθέσεων εκάστου, παροχή μερίσματος εις τους μετόχους του Ταμείου και τας οικογένειας αυτών…» κατά τρόπο δίκαιο και σύμφωνα με τις καταστατικές του διατάξεις εξασφαλίζοντας παράλληλα, λόγω της επιτασσόμενης αναλογίας του πόσου των καταθέσεων με το ποσό του μερίσματος, τη βιωσιμότητά του σε μακροπρόθεσμη βάση.</span></li>\r\n<li><span style="font-family: arial;">Παρέχει μέρισμα, επιστρέφει δηλαδή τις συσσωρευμένες καταθέσεις στους μετόχους και στις οικογένειές τους, εξυπηρετώντας τους με φιλικό και αξιόπιστο τρόπο με τη μεγαλύτερη δυνατή αποτελεσματικότητα, διαφάνεια και εντιμότητα, με σεβασμό στις νομικές προϋποθέσεις, χρησιμοποιώντας ολοένα και περισσότερο τις τεχνολογίες αιχμής και επενδύοντας σε διαρκή βάση στην επιμόρφωση και βέλτιστη αξιοποίηση του ανθρώπινου δυναμικού του.</span></li>\r\n</ol></p>', 1, 8, 0, 30, '2014-01-21 10:19:28', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-21 10:19:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'γενική λειτουργία\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'γενική λειτουργία του μετοχικού ταμείου πολιτικών υπαλλήλων', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(70, 'Διεύθυνση Διοικητικού', 'dieuthunse-dioiketikou', '<p><span style="text-align: justify; background-color: #e5e9f1;"><span style="font-family: arial;">Η Διεύθυνση Διοικητικού είναι επιφορτισμένη με τη διοικητική υποστήριξη των Υπηρεσιών του Ταμείου (λογιστήριο, επιμελητεία, μηχανογράφηση, τεχνική υπηρεσία κλπ). </span></span></p>', NULL, 1, 8, 0, 31, '2014-01-21 10:29:15', 62, NULL, '2014-01-21 10:48:56', 62, 0, '1979-12-19 00:00:00', '2014-01-21 10:29:00', '2060-01-01 00:00:00', NULL, 'Δείτε το οργανόγραμμα:|/mtpy/stoixeia_tameiou/diey8ynseis/organogramma.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 3, 0, 4, 'Διεύθυνση Διοικητικού\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'διεύθυνση διοικητικού', 29, 3, 'greek');
INSERT INTO `elx_content` VALUES(71, 'Διεύθυνση Ασφαλισμένων', 'dieuthunse-asphalismenon', '<p><span style="text-align: justify; background-color: #e5e9f1;"><span style="font-family: arial;">Η Διεύθυνση ασφαλισμένων είναι επιφορτισμένη με την εξυπηρέτηση των μελών του Ταμείου (μετόχων και μερισματούχων), και αρμόδια για την εφαρμογή της νομοθεσίας του.</span></span></p>', NULL, 1, 8, 0, 31, '2014-01-21 10:32:08', 62, NULL, '2014-01-21 10:50:02', 62, 0, '1979-12-19 00:00:00', '2014-01-21 10:32:00', '2060-01-01 00:00:00', NULL, 'Δείτε το οργανόγραμμα:|/mtpy/stoixeia_tameiou/diey8ynseis/organogramma.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 3, 'Διεύθυνση Ασφαλισμένων\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Διεύθυνση Ασφαλισμένων', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(72, 'Διεύθυνση  Διαχείρισης Περιουσίας', 'dieuthunse-diakheirises-periousias', '<p><span style="text-align: justify; background-color: #e5e9f1;"><span style="font-family: arial;">Η Διεύθυνση Διαχείρισης Περιουσίας είναι αρμόδια για την αξιοποίηση της κινητής και ακίνητης περιουσίας του Ταμείου, καθώς και για τον έλεγχο και την παρακολούθηση των εισροών (κρατήσεις και πόροι), των εκροών (μερίσματα) και των απαιτήσεών του (χρηματικές διευκολύνσεις).</span></span></p>', NULL, 1, 8, 0, 31, '2014-01-21 10:33:46', 62, NULL, '2014-01-21 10:50:23', 62, 0, '1979-12-19 00:00:00', '2014-01-21 10:33:00', '2060-01-01 00:00:00', NULL, 'Δείτε το οργανόγραμμα:|/mtpy/stoixeia_tameiou/diey8ynseis/organogramma.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 2, 'διεύθυνση διαχείρισης περιουσίας\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'διεύθυνση διαχείρισης περιουσίας', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(73, 'Διοικητικό Συμβούλιο  2012', 'dioiketiko-sumboulio-2012', '<p><span style="font-family: arial;">Το Διοικητικό Συμβούλιο του Ταμείου διοικεί και διαχειρίζεται την περιουσία του Μ.Τ.Π.Υ.  κατά την κρίση αυτού σύμφωνα με την ισχύουσα νομοθεσία.</span></p>', '<p><span style="font-family: arial;">Το παρόν Διοικητικό Συμβούλιο αποτελείται από τους κάτωθι:</span></p>\r\n<p><span style="font-family: arial;">1. Παύλος Περράτης, Πρόεδρος Δ.Σ. , πτυχιούχος Α.Ε.Ι.<br />2. Χαρίλαος Ξεζωνάκης, Αντιπρόεδρος Δ.Σ., πτυχιούχος Α.Ε.Ι.<br />3. Κωνσταντίνος Μπιτσικώκος, Μέλος, πτυχιούχος Α.Ε.Ι.<br />4. Κωνσταντίνος Χαραγκιώνης, Μέλος, πτυχιούχος Α.Ε.Ι., Υπάλληλος της Γ.Γ.Κ.Α. του Υπουργείου Εργασίας και Κοινωνικής Ασφάλισης.<br />5. Παναγιώτης Βαβουγυιός, Μέλος, Εκπρόσωπος Πολιτικών Συνταξιούχων, Πρόεδρος Π.Ο.Π.Σ..<br />6. Στέφανος Επαινετός, Εκπρόσωπος Εργαζομένων Μ.Τ.Π.Υ. <br />7. Γρηγόριος Καλομοίρης, Εκπρόσωπος της Α.Δ.Ε.Δ.Υ.</span></p>', 1, 8, 0, 31, '2014-01-21 10:36:40', 62, NULL, '2014-01-21 10:50:39', 62, 0, '1979-12-19 00:00:00', '2014-01-21 10:36:00', '2060-01-01 00:00:00', NULL, 'Δείτε το οργανόγραμμα:|/mtpy/stoixeia_tameiou/diey8ynseis/organogramma.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 1, 'διοικητικό συμβούλιο 2012\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Διοικητικό συμβούλιο 2012', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(74, 'Νομικό  Καθεστώς', 'nomiko-kathestos', '<p><span style="font-family: arial;">Διοικείται από Διοικητικό Συμβούλιο, σύμφωνα με τις διατάξεις του Π.Δ. 422/81.</span></p>', '<p><span style="font-family: arial;">Το Μ.Τ.Π.Υ. είναι αποκεντρωμένη δημόσια υπηρεσία, λειτουργεί ως ειδικό Ταμείο του κράτους, έχει ίδια νομική προσωπικότητα, απολαμβάνει διοικητική αυτοτέλεια και οικονομική αυτοδυναμία, έχει ίδιο προϋπολογισμό που δεν εντάσσεται στον κρατικό ή κοινωνικό προϋπολογισμό και διεξάγει το ίδιο τις δίκες που το αφορούν, έχοντας την ικανότητα να είναι διάδικος.Οι καταθέσεις των μετόχων δεν αποτελούν ασφαλιστική εισφορά αλλά συμμετοχή στα κεφάλαια του Ταμείου. Η συμμέτοχη αυτή αποτελεί και τη βασική προϋπόθεση για να έχουν δικαίωμα επιστροφής των κεφαλαίων τους υπό μορφή μερίσματος.</span></p>', 1, 8, 0, 32, '2014-01-21 11:20:49', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-21 11:20:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 3, 'νομικό καθεστώς μετοχικού ταμείου πολιτικών υπαλλήλων', 'νομικό καθεστώς μετοχικού ταμείου πολιτικών υπαλλήλων', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(75, 'Ν.4024/2011', 'n40242011', '<p><span style="font-family: arial;">Με βάση τον Ν.4024/2011 υπάγεται στην εποπτεία του Υπουργείου Εργασίας και Κοινωνικής Ασφάλισης.</span></p>', '<p><span style="font-family: arial;">Το Μ.Τ.Π.Υ δεν είναι ασφαλιστικός οργανισμός, έχει εξαιρεθεί ρητά σύμφωνα με τη διάταξη του άρθρου 11 παρ.2 του Ν. 2227/1994 από το συνταξιοδοτικό νόμο 2084/1992, με την αιτιολογία ότι δεν αποτελεί φορέα κοινωνικής ασφάλισης αλλά πρόκειται περί ειδικού ταμείου του Κράτους, όπως προκύπτει από το άρθρο 1 του ΠΔ 422/81 και της παρ.1 του άρθρου 19 του Ν.2386 /1936, όπως αντικαταστάθηκε με την παρ. 5 του άρθρου 12 του Ν. 2443/1996, στο οποίο μετέχουν υποχρεωτικά όλοι οι πολιτικοί υπάλληλοι του Δημοσίου, έχει οικονομική αυτοδυναμία και δεν επιβαρύνει τον Κρατικό Προϋπολογισμό, αλλά στηρίζεται στις  καταθέσεις των μετόχων του και σε ίδιους πόρους (εισηγητική έκθεση του ανώτερου νόμου, γνωμοδότηση ΝΣΚ 200/2008  και 247/2008).</span></p>', 1, 8, 0, 32, '2014-01-21 11:23:24', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-21 11:23:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 2, 'Ν.4024/2011  νομικό καθεστώς\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Ν.4024/2011  νομικό καθεστώς', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(76, 'Ν.4052/2012', 'n40522012', '<p><span style="font-family: arial;">Το Ταμείο λειτουργεί μέσα σε ένα εκ των προτέρων θεσμοθετημένο πλαίσιο (Π.Δ. 422/81), το οποίο οριοθετεί και την οποιαδήποτε δράση ή ενέργειά του.</span></p>', '<p><span style="font-family: arial;">Με τον Ν.4052/2012 προσετέθη διάταξη με βάση την οποία όπου στην ισχύουσα νομοθεσία προβλέπεται υπαγωγή, κατά οποιανδήποτε τρόπο, του Μετοχικού Ταμείου Πολιτικών Υπάλληλων  (Μ.Τ.Π.Υ.) στην αρμοδιότητα του Υπουργείου Οικονομικών, νοείται από την έναρξη ισχύος του ν. 4024/2011 (Α’ 226) το Υπουργείο Εργασίας και Κοινωνικής Ασφάλισης, ιδίως στις διατάξεις της παραγράφου 1 του άρθρου 19 του ν. 2386/1996 (Α’43) και στις διατάξεις του π.δ. 94/2011 (Α΄225). Επίσης αντικαταστάθηκε η περίπτωση α΄ της παραγράφου 1 του άρθρου 3 του Ν.4024/2011 (Α΄226) ως εξής: «α. Από την έναρξη ισχύος του νόμου αυτού, το Μετοχικό Ταμείο Πολιτικών Υπάλληλων (Μ.Τ.Π.Υ.) υπάγεται στην αρμοδιότητα του Υπουργείου Εργασίας και Κοινωνικής Ασφάλισης και εποπτεύεται από την Γενική Γραμματεία Κοινωνικών Ασφαλίσεων».</span></p>', 1, 8, 0, 32, '2014-01-21 11:26:10', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-21 11:26:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'Ν.4052/2012  νομικό καθεστώς\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Ν.4052/2012  νομικό καθεστώς', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(77, 'Πολιτικοί υπάλληλοι', 'politikoi-upalleloi', '<p><span style="font-family: arial;">Στο Μ.Τ.Π.Υ. μετέχουν υποχρεωτικά όλοι οι δόκιμοι και μόνιμοι πολιτικοί υπάλληλοι των Υπουργείων και της Βουλής, εκτός όσων εξαιρούνται δια νόμου. Από τους υπαλλήλους των Ν.Π.Δ.Δ. μετέχουν μόνο όσοι με ειδικές διατάξεις έχουν υπαχθεί στην ασφάλιση του Μ.Τ.Π.Υ.</span></p>', NULL, 1, 9, 0, 33, '2014-01-22 07:16:32', 62, NULL, '2014-01-22 07:18:14', 62, 0, '1979-12-19 00:00:00', '2014-01-22 07:16:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 3, 'Πολιτικοί υπάλληλοι \r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Πολιτικοί υπάλληλοι', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(78, 'Πυροσβεστικό σώμα', 'purosbestiko-soma', '<p><span style="font-family: arial;">Επίσης, μετέχει το προσωπικό (διοικητικό και στρατιωτικό) του Πυροσβεστικού Σώματος.</span></p>', NULL, 1, 9, 0, 33, '2014-01-22 07:18:04', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 07:18:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 2, 'Πυροσβεστικό σώμα\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Πυροσβεστικό σώμα', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(79, 'ΕΛ.ΑΣ', 'elas', '<p><span style="font-family: arial;">Επίσης, μετέχει το πολιτικό προσωπικό της ΕΛ.ΑΣ. και οι αστυνομικοί που έχουν υπαχθεί στα ασφαλιστικά ταμεία της πρώην Αστυνομίας Πόλεων.</span></p>', NULL, 1, 9, 0, 33, '2014-01-22 07:19:45', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 07:19:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'ΕΛ.ΑΣ αστυνομικοί \r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'ΕΛ.ΑΣ αστυνομικοί', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(80, 'Αναστολή  χρηματοδότησης  2014', 'anastole-khrematodoteses-2014', '<p><span style="font-family: arial;">Η παροχή χρηματικών διευκολύνσεων έχει ανασταλεί και για το έτος 2014 με απόφαση του Διοικητικού Συμβούλιου επειδή οι εισροές του Ταμείου από τις καταθέσεις των Μετόχων έχουν μειωθεί σημαντικά και δεν επαρκούν να καλύψουν τα διαρκώς αυξανόμενα μερίσματα, επειδή εξέρχονται της υπηρεσίας  μεγάλος αριθμός υψηλόμισθων μετόχων με υψηλές κρατήσεις και αφ έτερου επειδή οι κρατήσεις  που πραγματοποιούνται στους νέους μετόχους είναι μειωμένες γιατί προέρχονται από μικρό αριθμό χαμηλόμισθων νέων  μετόχων.</span></p>', NULL, 1, 9, 0, 34, '2014-01-22 07:26:37', 62, NULL, '2014-01-22 07:32:10', 62, 0, '1979-12-19 00:00:00', '2014-01-22 07:26:00', '2060-01-01 00:00:00', NULL, 'Δείτε την σχετική απόφαση:|/mtpy/stoixeia_metoxoi/xrhmatodothsh/apofasi2014.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 3, 0, 1, 'Αναστολή χρηματοδότησης 2014\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Αναστολή χρηματοδότησης 2014', 29, 2, 'greek');
INSERT INTO `elx_content` VALUES(81, 'Δικαιολογητικά', 'dikaiologetika', '<p><span style="font-family: arial;">Η αναγνώριση γίνεται μετά από αίτηση του μετόχου. Για κάθε αναγνωριζόμενο μήνα καταβάλλεται εισφορά 4% επί του βασικού μισθού και του χρονοεπιδόματος  που λαμβάνει ο μέτοχος κατά την ημέρα υποβολής της αίτησης.</span></p>\r\n<p><span style="font-family: arial;">Δικαιολογητικά που πρέπει να υποβληθούν στο ΜΤΠΥ για αναγνώριση προϋπηρεσίας:</span></p>', '<p></p>\r\n<ol>\r\n<li><span style="font-family: arial;">Αίτηση – Υπεύθυνη Δήλωση.</span></li>\r\n<li><span style="font-family: arial;">Πιστοποιητικό υπηρεσιακών μεταβολών , στο οποίο θ'' αναφέρεται το ΦΕΚ διορισμού, η ανακοίνωση του διορισμού και η ορκωμοσία , η προϋπηρεσία και τα χρονικά διαστήματα αυτής, η ιδιότητα (π.χ. έκτακτος, ημερομίσθιος κλπ.) και η σχέση εργασίας (π.χ. δημοσίου ή ιδιωτικού δικαίου κλπ.) καθώς και οι διατάξεις με τις οποίες παρασχέθηκε η προϋπηρεσία.</span></li>\r\n<li><span style="font-family: arial;">Αναλυτική βεβαίωση αποδοχών και κρατήσεων του μήνα υποβολής της αίτησης.</span></li>\r\n<li><span style="font-family: arial;">Βεβαίωση της ημερομηνίας έναρξης των υπέρ ΜΤΠΥ κρατήσεων (τακτικής κράτησης και δικαιώματος εγγραφής).</span></li>\r\n<li><span style="font-family: arial;">Πιστοποιητικό Στρατολογικού Γραφείου τύπου Α'' εφόσον μέχρι 31/12/1997 θεμελιώνεται συνταξιοδοτικό δικαίωμα ( 25 έτη υπηρεσίας συνυπολογιζομένης και της στρατιωτικής θητείας ), διαφορετικά πράξη εξαγοράς της στρατιωτικής θητείας στο Γενικό Λογιστήριο του Κράτους  (Ν.2084/92).</span></li>\r\n<li><span style="font-family: arial;">Πράξη αναγνώρισης του ιδιωτικού τομέα στο ΓΛΚ, εφόσον ο διορισμός στο δημόσιο έγινε μέχρι 31/12/1982.</span></li>\r\n<li><span style="font-family: arial;">Πιστοποιητικό του οικείου ασφαλιστικού φορέα, του Ν. 1405/83 από το οποίο θα προκύπτει ο χρόνος ασφάλισης για υπηρεσία στον ιδιωτικό τομέα (έτη, μήνες, ημέρες ή το σύνολο των ημερομισθίων ), εφόσον ο διορισμός στο δημόσιο γίνεται μετά την 1/1/1983.</span></li>\r\n<li><span style="font-family: arial;">Βεβαίωση επικουρικού ταμείου στο οποίο ήταν ασφαλισμένος κατά το χρόνο της προϋπηρεσίας (ΤΕΑΜ κλπ.), σε περίπτωση που επιθυμεί για κάποιο διάστημα την εφαρμογή διαδοχικής ασφάλισης με το ΜΤΠΥ. (Σημείωση: Η διαδοχική ασφάλιση εφαρμόζεται ΜΟΝΟ σ'' ένα επικουρικό Ταμείο).</span></li>\r\n</ol>\r\n<p></p>', 1, 9, 0, 35, '2014-01-22 09:11:54', 62, NULL, '2014-01-22 09:18:18', 62, 0, '1979-12-19 00:00:00', '2014-01-22 09:11:00', '2060-01-01 00:00:00', NULL, 'Δείτε την σχετική αίτηση|/mtpy/stoixeia_metoxoi/proyphresia/anagnwrish_aithsh.doc\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 3, 0, 2, 'Αναγνώριση προϋπηρεσία\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Αναγνώριση προϋπηρεσίας', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(82, 'Οφειλή', 'opheile', '<p><span style="font-family: arial;">Η εξόφληση της οφειλής γίνεται:</span></p>', '<p></p>\r\n<ol>\r\n<li><span style="font-family: arial;">Εντός τριμήνου από την κοινοποίηση του λογαριασμού οπότε παρέχεται έκπτωση 10% στο ποσόν της οφειλής από αναγνώριση υπηρεσίας.</span></li>\r\n<li><span style="font-family: arial;">Εντός ενός έτους από την κοινοποίηση του λογαριασμού εφάπαξ, ή τμηματικά χωρίς καμία επιβάρυνση από τόκους. Οι πληρωμές γίνονται είτε απευθείας στο ΜΤΠΥ, είτε με ταχυδρομικές επιταγές στη διεύθυνση: ΜΤΠΥ - Τμήμα Μετόχων, Λυκούργου 12, ΤΚ 105 51, Αθήνα.</span></li>\r\n</ol>\r\n<p></p>\r\n<p><span style="font-family: arial;">Οι οφειλές που δεν καταβάλλονται εντός έτους από την κοινοποίηση του λογαριασμού αναπροσαρμόζονται βάσει των αποδοχών του μήνα, που θα ζητηθεί με νέα αίτηση η εξόφληση.</span></p>\r\n<p></p>', 1, 9, 0, 35, '2014-01-22 09:14:19', 62, NULL, '2014-01-22 09:18:05', 62, 0, '1979-12-19 00:00:00', '2014-01-22 09:14:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 1, 'Οφειλή προϋπηρεσίας\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Οφειλή προϋπηρεσίας', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(83, 'Προϋποθέσεις', 'proupotheseis', '<p><span style="font-family: arial;">Προϋποθέσεις θεμελίωσης ατομικού δικαιώματος για μέρισμα</span></p>', '<p><span style="font-family: arial;">Ο μέτοχος θεμελιώνει δικαίωμα για μέρισμα αν: </span></p>\r\n<p><ol>\r\n<li><span style="font-family: arial;">απομακρυνθεί από την Υπηρεσία έχει 20 χρόνια συνολικής συμμετοχής στην Ασφάλιση του Ταμείου και δικαιωθεί συντάξεως.</span></li>\r\n<li><span style="font-family: arial;">απολυθεί από την Υπηρεσία λόγω ορίου ηλικίας ή 35ετίας ή κατάργησης της θέσης κι έχει 15 χρόνια συνολικής συμμετοχής</span></li>\r\n<li><span style="font-family: arial;">δικαιωθεί σύνταξης από το Δημόσιο λόγω σωματικής ή διανοητικής ανικανότητας που δεν οφείλεται στην Υπηρεσία κι έχει 15 χρόνια συνολικής συμμετοχής</span></li>\r\n<li><span style="font-family: arial;">δικαιωθεί σύνταξης από το Δημόσιο λόγω σωματικής ή διανοητικής ανικανότητας, που οφείλεται στην Υπηρεσία και συμπληρώσει μ'' εξαγορά 15ετή συμμετοχή, ανεξάρτητα από τον χρόνο συμμετοχής που έχει στο Ταμείο</span></li>\r\n<li><span style="font-family: arial;">είναι έγγαμη γυναίκα ή χήρα ή διαζευγμένη με ανήλικα παιδιά, έχει κατά την ημέρα εξόδου από την Υπηρεσία 15ετή συμμετοχή κι εξαγοράζει το υπόλοιπο χρονικό διάστημα μέχρι την 20ετία. </span></li>\r\n</ol></p>\r\n<p></p>', 1, 9, 0, 36, '2014-01-22 09:33:41', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 09:33:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 5, 'Προϋποθέσεις κανονισμού μερίσματος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Προϋποθέσεις κανονισμού μερίσματος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(84, 'Δικαιολογητικά', 'dikaiologetika', '<p>Τα δικαιολογητικά είναι τα παρακάτω:</p>', '<p><ol>\r\n<li><span style="font-family: arial;">δελτίο ατομικής και υπηρεσιακής κατάστασης του υπαλλήλου, που συντάσσεται από τη Δ/νση Διοικητικού του υπαλλήλου που συνταξιοδοτείτε</span></li>\r\n<li><span style="font-family: arial;"> αντίγραφο της συνταξιοδοτικής Πράξης του Γενικού Λογιστηρίου του Κράτους με την οποία κανονίσθηκε σύνταξη από το Δημόσιο, το οποίο διαβιβάζεται πλέον υπηρεσιακά από το Γενικό Λογιστήριο του Κράτους χωρίς τη μεσολάβηση του ενδιαφερομένου.</span></li>\r\n</ol></p>', 1, 9, 0, 36, '2014-01-22 09:36:08', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 09:36:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 4, 'δικαιολογητικά κανονισμός μερίσματος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'δικαιολογητικά κανονισμός μερίσματος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(85, 'Αρχές Υπολογισμού', 'arkhes-upologismou', '<p><span style="font-family: arial;">Οι βασικές αρχές που διέπουν τον υπολογισμό μερίσματος καθώς και ο τρόπος περιγράφονται παρακάτω:</span></p>', '<p><span style="font-family: arial;">Ο υπολογισμός και η απονομή του μερίσματος πρέπει να είναι σύμφωνος με τον προορισμό του Tαμείου μέσα στα πλαίσια της ισχύουσας νομοθεσίας και γι’ αυτό πρέπει  να  ικανοποιεί  πέντε βασικές αρχές:</span></p>\r\n<p></p>\r\n<ol>\r\n<li><span style="font-family: arial;">να είναι δίκαιος,</span></li>\r\n<li><span style="font-family: arial;">να είναι σύμφωνος με τις καταστατικές διατάξεις του Tαμείου,</span></li>\r\n<li><span style="font-family: arial;"> να μην λειτουργεί σε βάρος καμίας κατηγορίας μεταξύ των μετόχων  και των    μερισματούχων, </span></li>\r\n<li><span style="font-family: arial;">να εξασφαλίζει την βιωσιμότητα του Tαμείου σε μακροπρόθεσμη βάση και τέλος </span></li>\r\n<li><span style="font-family: arial;">να υπολογίζεται και να καταβάλλεται το ταχύτερο δυνατόν. </span></li>\r\n</ol>\r\n<p></p>\r\n<div>\r\n<div><span style="font-family: arial;">Με τους νόμους 4024/2011 και 4052/2012 αντικαταστάθηκαν οι Συντάξιμες Αποδοχές ΣΑ με τον Βασικό Μισθό ΒΜ και ο υπολογισμός του μερίσματος έγινε αναλογικός ως προς τα άλλα επιδόματα, με βάση το χρόνο συμμετοχής κάθε επιδόματος, καθιερώθηκε δε και ο μήνας συμμετοχής. </span></div>\r\n<div><span style="font-family: arial;">Η τροποποίηση του άρθρου 49 του Π.Δ. 422/81 που πραγματοποιήθηκε με το άρθρο 2 του Ν. 4024/2011 απέβλεπε στη διόρθωση της εσφαλμένης διατύπωσης του άρθρου 3 του Ν. 3336/2005, που είχε σαν αποτέλεσμα την απομάκρυνση του Ταμείου από τη βασική καταστατική του διάταξη της «…κατ αναλογία του ποσού των καταθέσεων εκάστου, παροχή μερίσματος εις τους μετόχους και τας οικογένειας αυτών …».</span></div>\r\n<div><span style="font-family: arial;">Ο υπολογισμός του μερίσματος βασίζεται στο βασικό μισθό του μισθολογικού κλιμακίου ή βαθμού που έφερε ο υπάλληλος κατά την έξοδο από την υπηρεσία.</span></div>\r\n<div><span style="font-family: arial;">Έτσι, το άρθρο 49 του π.δ. 422/1981, αντικαθίσταται με την παράγραφο 3 του άρθρου 3 του Ν. 4024/2011 ως εξής:</span></div>\r\n<div><span style="font-family: arial;"><br /></span></div>\r\n<div><ol>\r\n<li><span style="font-family: arial;">Το μηνιαίο μέρισμα (Μ) των μετόχων του Μ.Τ.Π.Υ. είναι ίσο με το άθροισμα:</span></li>\r\n</ol></div>\r\n<div style="text-align: justify;">\r\n<ul>\r\n<li><span style="font-family: arial;"> του 20% του βασικού μισθού με το επίδομα χρόνου υπηρεσίας επί τα έτη συμμετοχής στην ασφάλιση του Ταμείου, δια 35, επί το συντελεστή προσαρμογής (Σ) 0,8075 (α΄υπομέρισμα) και</span></li>\r\n<li><span style="font-family: arial;">του 20% των «τυχόν άλλων αποδοχών» επί τα έτη συμμετοχής εκάστων στην ασφάλιση του Ταμείου, δια 35, επί το συντελεστή προσαρμογής (Σ) 0,8075 (β΄υπομέρισμα).</span></li>\r\n</ul>\r\n<span style="font-family: arial;"> 2. Απαραίτητη προϋπόθεση προκειμένου να ληφθούν υπόψη για τον κανονισμό του μερίσματος, ο βασικός μισθός, το επίδομα χρόνου υπηρεσίας και οι τυχόν άλλες αποδοχές, είναι να έχουν υποβληθεί οι αποδοχές αυτές στη προβλεπόμενη από τις διατάξεις του άρθρου 26, παρ. 1 περίπτωση α΄του π.δ. 422/81, όπως εκάστοτε ισχύει, κράτηση υπέρ Μ.Τ.Π.Υ. Ουδεμία μισθολογική απολαβή ή τμήμα της δεν λαμβάνεται υπόψη για τον κανονισμό, εάν δεν έχει υποβληθεί στην κράτηση που προαναφέρεται.</span></div>\r\n<div><span style="font-family: arial;">Οι παράγραφοι 3,4 και 5 του άρθρου 49 του Π.Δ. 422/1981 (Α΄114) όπως αντικαταστάθηκε με την παράγραφο 3 του άρθρου 3 του Ν. 4024/2011 (Α΄226), αντικαθίστανται ως εξής:</span></div>\r\n</div>\r\n<div><br />\r\n<div><ol>\r\n<li><span style="font-family: arial;">Ως βασικός μισθός για την εφαρμογή του παρόντος άρθρου νοείται: α) έτη υπηρεσίας έως 31/10/2011, ο βασικός μισθός του ΜΚ ή βαθμού και το χρονοεπίδομα που έφερε ο υπάλληλος την 31/10/2011 και β) για τα έτη υπηρεσίας που αντιστοιχούν από 01/11/2011 και μέχρι την έξοδο, ο βασικός μισθός του ΜΚ ή βαθμού και χρονοεπιδόματος που φέρει ο υπάλληλος κατά την έξοδό του από την υπηρεσία, τα οποία υπολογιζόμενα σύμφωνα με την παράγραφο 1 δίνουν το ποσό α΄υπομερίσματος.</span></li>\r\n<li><span style="font-family: arial;">Ως «τυχόν άλλες αποδοχές» νοούνται τα επιδόματα, οι αποζημιώσεις και τα ποσά που συνεντέλλονται με τις συντάξιμες αποδοχές, αναλόγως των μηνών κατά τα οποία διενεργήθηκε επ’ αυτών η προβλεπόμενη κράτηση από την περίπτωση α΄ της παραγράφου 1 του άρθρου 26 του πδ 422/81, όπως εκάστοτε ισχύει, υπέρ του Μ.Τ.Π.Υ. και οι οποίες υπολογιζόμενες σύμφωνα με την παράγραφο 1 δίνουν το ποσό β΄ υπομερίσματος.</span></li>\r\n<li><span style="font-family: arial;">Το άθροισμα των ποσών των υπομερισμάτων της παρ. 1, όπως αναλύονται στις παρ. 3 και 4, αποτελεί το συνολικό ποσό του δικαιούμενου μερίσματος, σύμφωνα με τους ακόλουθους μαθηματικούς τύπους, οι οποίοι βέβαια μπορούν να αναπτυχθούν σε περισσότερους ανάλογα με τα κατά περίπτωση επιδόματα – αποζημιώσεις:</span></li>\r\n</ol>\r\n<div><span style="font-family: arial;">Α) α΄υπομέρισμα =(Α.α.+Α.β)</span></div>\r\n<div><span style="font-family: arial;">Α.α 20%*(ΒΜ+επιδομα χρόνου υπηρεσίας έως 31.10.2011)*[έτη έως 31.10.2011/35]*Σ</span></div>\r\n<div><span style="font-family: arial;">Α.β 20%*(ΒΜ+επιδομα χρόνου υπηρεσίας έως 01.11.2011)*[έτη από 01.11.2011/35]*Σ</span></div>\r\n<div><span style="font-family: arial;">Β) β΄υπομέρισμα = 20%*(«τυχόν άλλες αποδοχές»)*[μήνες/420]*Σ</span></div>\r\n<div><span style="font-family: arial;">Μηνιαίο μέρισμα (Μ)=(Α΄υπομέρισμα +Β΄υπομέρισμα)</span></div>\r\n<br style="font-family: arial;" /><span style="font-family: arial;"> </span></div>\r\n</div>', 1, 9, 0, 36, '2014-01-22 09:47:52', 62, NULL, '2014-01-22 09:58:04', 62, 0, '1979-12-19 00:00:00', '2014-01-22 09:47:00', '2060-01-01 00:00:00', NULL, 'Δείτε το σχετικό έντυπο|/mtpy/stoixeia_metoxoi/kanonismos_merismatos/dayk.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 3, 'αρχές υπολογισμού μερίσματος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Αρχές υπολογισμού μερίσματος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(86, 'Διάρκεια Διακοπή', 'diarkeia-diakope', '<p><span style="font-family: arial;">Το δικαίωμα για μέρισμα αρχίζει από την επόμενη της ημέρας διακοπής της μισθοδοσίας ή εφόσον καταβλήθηκαν τρίμηνες αποδοχές από την επόμενη λήξης αυτών ή σε περίπτωση αναστολής της σύνταξης την ημερομηνία που ορίζει η συνταξιοδοτική πράξη του Γ.Λ.Κ. </span></p>', '<p><span style="font-family: arial;">Όσο αφορά την διακοπή του μερίσματος αυτό συμβαίνει σε περίπτωση επανόδου του μετόχου στην υπηρεσία, ή λόγω θανάτου. Αν ο μέτοχος πεθάνει στην Υπηρεσία και δεν έχει συμπληρώσει το ελάχιστο όριο συμμετοχής για θεμελίωση δικαιώματος μερίσματος τα μέλη της ορφανικής οικογένειας του εφόσον θα είχαν δικαίωμα μερίσματος από μεταβίβαση, δικαιούνται όλα μαζί "εφ'' άπαξ βοήθημα" ως εξής:</span></p>\r\n<p>\r\n<ul>\r\n<li><span style="font-family: arial;">Για συμμετοχή μέχρι 3 έτη 1467,35 €</span></li>\r\n<li><span style="font-family: arial;">Για συμμετοχή από 3 - 6 έτη 2201,03 €</span></li>\r\n<li><span style="font-family: arial;">Για συμμετοχή από 6 και άνω 2934,70 €.</span></li>\r\n</ul>\r\n</p>\r\n<p><span style="font-family: arial;">Η αξίωση της παροχής αυτής παραγράφεται ύστερα από 2 χρόνια από το τέλος του έτους που απεβίωσε ο μέτοχος.</span></p>\r\n<div></div>\r\n<div></div>', 1, 9, 0, 36, '2014-01-22 09:51:47', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 09:51:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 2, 'Διάρκεια Διακοπή κανονισμού μερίσματος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Διάρκεια Διακοπή κανονισμού μερίσματος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(87, 'Αναθεώρηση', 'anatheorese', '<p><span style="font-family: arial;">Οι αποφάσεις του Διοικητικού Συμβουλίου του ΜΤΠΥ για απονομή μερίσματος υπόκεινται σε αναθεώρηση με αίτηση προς το Διοικητικό Συμβούλιο, εντός έτους από την ημερομηνία κοινοποίησής τους.</span></p>\r\n<div></div>', '<p><span style="font-family: arial;">Βασική προϋπόθεση για τον κανονισμό, την αναθεώρηση ή τη μεταβίβαση του μερίσματος του ΜΤΠΥ είναι έκδοση αντίστοιχης Συνταξιοδοτικής Πράξης από την αρμόδια Υπηρεσία του Γ.Λ.Κ., μιας και τα στοιχεία που αναγράφονται σε αυτή (συντάξιμος μισθός, έτη) είναι απαραίτητα για τον υπολογισμό του μερίσματος.</span></p>\r\n<div></div>', 1, 9, 0, 36, '2014-01-22 09:53:21', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 09:53:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'Αναθεώρηση κανονισμού μερίσματος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Αναθεώρηση κανονισμού μερίσματος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(88, 'Ευρωπαϊκό  ασφαλιστικό', 'europaiko-asphalistiko', '<p><span style="font-family: arial;">Η διαδοχική ασφάλιση στο συνταξιοδοτικό ευρωπαϊκό σύστημα γίνεται ως εξής:</span></p>', '<p><span style="font-family: arial;">Ασφαλισμένος του ΜΤΠΥ, που αναλαμβάνει Υπηρεσία ως υπάλληλος στις Ευρωπαϊκές Κοινότητες μπορεί να μεταφέρει, με αίτησή του προς την Διοίκηση των Ευρωπαϊκών Κοινοτήτων, στο Κοινοτικό Σύστημα τα ασφαλιστικά του δικαιώματα, που απέκτησε πριν από την είσοδό του στις Υπηρεσίες των Ευρωπαϊκών Κοινοτήτων. Το δικαίωμα αυτό μπορεί να το ασκήσει και εκείνος ο οποίος κατά την διάρκεια κανονικής αδείας άνευ αποδοχών απέκτησε το συνταξιοδοτικό του δικαίωμα. Οργανισμός Σύνδεσης για τους ασφαλισμένους του ΜΤΠΥ είναι το Γ.Λ.Κ. δια της 47ης Δ/νσης Συνταξιοδοτικού, Εργασίας, Συστάσεων και προσφυγών. Η αίτηση μεταφοράς των συνταξιοδοτικών δικαιωμάτων διαβιβάζεται στο ΜΤΠΥ μέσω του οργανισμού σύνδεσης (ΓΛΚ) μαζί μ'' απαραίτητα δικαιολογητικά του ασφαλισμένου, δηλαδή:<br /></span></p>\r\n<ol>\r\n<li>Αίτηση (download αίτηση).</li>\r\n<li>Αντίγραφο της αποφάσεως του Γενικού Λογιστηρίου του Κράτους, περί κανονισμού συντάξεων, νόμιμα θεωρημένο.</li>\r\n<li>Πιστοποιητικό υπηρεσιακών μεταβολών</li>\r\n<li>Πιστοποιητικό Στρατολογικού Γραφείου τύπου Α΄, εφ'' όσον πρόκειται για άνδρα. Αν η στρατιωτική υπηρεσία συμπίπτει με την πολιτική υπηρεσία θα υποβάλει και βεβαίωση στην οποία θα φαίνεται εάν έπαιρνε τις αποδοχές της πολιτικής θέσης.</li>\r\n<li>Υπεύθυνη δήλωση ότι δεν παίρνει άλλο μέρισμα από το ΜΤΠΥ για οποιαδήποτε λόγο, εφ'' όσον πρόκειται για γυναίκα.</li>\r\n<li>Βεβαίωση αποδοχών του τελευταίου μήνα της μισθοδοσίας, πριν από την είσοδο του στην Διοίκηση Ευρωπαϊκών Κοινοτήτων</li>\r\n<li>Βεβαίωση προϋπηρεσίας, πριν από την μονιμοποίηση, στην οποία θα φαίνεται με ποία σχέση εργασίας υπηρέτησε (δημοσίου ή ιδιωτικού δικαίου) με ποια ιδιότητα (μηνιαίο μισθό ή ημερομίσθιο) και εάν έγιναν κρατήσεις για το ΜΤΠΥ και σε ποιο ποσοστό (1%, 2%, 3%) κατά την διάρκεια της.</li>\r\n<li>Εκπαιδευτικοί οι οποίοι υπηρέτησαν σε Σχολεία της Αλλοδαπής, με απόσπαση θα υποβάλουν απαραιτήτως βεβαίωση για τη διάρκεια αυτής και εάν ήταν η απόσπαση μετ'' αποδοχών ή άνευ.</li>\r\n<li>Βεβαίωση αρμόδιου Επικουρικού Ταμείου (για προϋπηρεσία που εφαρμόζονται οι διατάξεις του Ν.Δ. 4202/61 και του Ν. 1405/83, περί διαδοχικής ασφάλισης κ.λ.π.) στην οποία φαίνεται ο χρόνος ασφάλισης, εάν δικαιώθηκε σύνταξης ή του έγινε επιστροφή ασφαλιστικών εισφορών απ'' το Ταμείο αυτό.</li>\r\n</ol>\r\n<p></p>\r\n<p><span style="font-family: arial;">Εάν ο μέτοχος απεβίωσε στην υπηρεσία: </span></p>\r\n<p><span style="font-family: arial;">Η ορφανική οικογένεια πρέπει να υποβάλει τα ίδια δικαιολογητικά που αναφέρονται παραπάνω (Α΄). Στην περίπτωση που ο μέτοχος (Α,Β) δεν δικαιούται Συντάξεως, θα υποβάλλονται όλα τα δικαιολογητικά, που αναφέρονται, εκτός από το αντίγραφο της αποφάσεως του Γενικού Λογιστηρίου του Κράτους. <br /></span></p>\r\n<p></p>\r\n<ol>\r\n<li><span style="font-family: arial;">Αίτηση.</span></li>\r\n<li><span style="font-family: arial;">Αντίγραφο της αποφάσεως του Γενικού Λογιστηρίου του Κράτους, περί κανονισμού συντάξεως της ορφανικής οικογένειας.</span></li>\r\n<li><span style="font-family: arial;">Πιστοποιητικό οικογενειακής καταστάσεως του Δήμου ή Κοινότητας, που ανήκε ο αποβιώσας, στο οποίο να φαίνεται με ακρίβεια:</span></li>\r\n</ol>\r\n<p></p>\r\n<p></p>\r\n<ul>\r\n<li><span style="font-family: arial;">η ημερομηνία του θανάτου του μερισματούχου,</span></li>\r\n<li><span style="font-family: arial;">εάν ο γάμος με την αιτούσα χήρα ήταν α΄, β΄ ή γ΄ και πότε έγινε,</span></li>\r\n<li><span style="font-family: arial;">το ονόματα και η προσωπική κατάσταση των παιδιών (ημερομηνία γεννήσεως για τα αγόρια και για τα κορίτσια αν είναι άγαμα ή έγγαμα),</span></li>\r\n<li><span style="font-family: arial;">εάν ο γάμος, εξακολούθησε να υφίσταται από τότε που έγινε μέχρι και του θανάτου του, χωρίς να διαλυθεί με διαζύγιο</span></li>\r\n<li><span style="font-family: arial;">εάν η αιτούσα εξακολουθεί να είναι χήρα.</span></li>\r\n</ul>\r\n<p></p>\r\n<p><span style="font-family: arial;"> 4. Υπεύθυνη δήλωση της ορφανικής οικογένειας όπως αναφέρεται στο (Α5) στοιχείο.<br /><br />Το ΜΤΠΥ είναι υποχρεωμένο μέσα σε προθεσμία 6μηνών από την ημερομηνία λήψης της παραπάνω αίτησης να κοινοποιήσει στην Διοίκηση των Ευρωπαϊκών Κοινοτήτων και στον ενδιαφερόμενο το ύψος του προς μεταφορά κεφαλαίου (αναλογιστικό ισοδύναμο) καθώς και τις περιόδους ασφάλισης. Ο ενδιαφερόμενος πρέπει μέσα σε προθεσμία 12 μηνών από την ημερομηνία κοινοποίησης του εγγράφου να δηλώσει εγγράφως στο ΜΤΠΥ μέσω της Διοίκησης των Ευρωπαϊκών Κοινοτήτων, εάν αποδέχεται τη μεταφορά του αναλογιστικού ισοδύναμου από το Ελληνικό Συνταξιοδοτικό Σύστημα στο Συνταξιοδοτικό Σύστημα των Υπαλλήλων των Ευρωπαϊκών Κοινοτήτων Η δήλωση αποδοχής είναι οριστική και αμετάκλητη. Η πραγματική μεταφορά του κεφαλαίου γίνεται μέσα σε προθεσμία 6 μηνών από την ημερομηνία οριστικής δήλωσης αποδοχής. Υπογραμμίζεται ότι οι αιτήσεις για πάσης φύσεως αναγνωρίσεις (χρόνος προϋπηρεσίας Ιδιωτικού δικαίου, ημερομίσθια κλπ. Και στρατιωτική υπηρεσία) πρέπει να έχουν υποβληθεί και οι σχετικές οφειλές να έχουν εξοφληθεί προς το ΜΤΠΥ, πριν από την υποβολή στην Διοίκηση των Ευρωπαϊκών Κοινοτήτων της αίτησης για μεταφορά του αναλογιστικού του ισοδυνάμου. <br /> </span></p>\r\n<p><span style="font-family: arial;"> </span></p>\r\n<p><span style="font-family: arial;"> </span></p>', 1, 9, 0, 37, '2014-01-22 10:12:00', 62, NULL, '2014-01-22 10:13:31', 62, 0, '1979-12-19 00:00:00', '2014-01-22 10:12:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 3, 'ευρωπαϊκό σύστημα διαδοχικής ασφάλισης\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'ευρωπαϊκό σύστημα διαδοχικής ασφάλισης', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(89, 'Ελληνικό ασφαλιστικό', 'elleniko-asphalistiko', '<p>Η διαδοχική ασφάλιση στο ελληνικό συνταξιοδοτικό σύστημα:</p>', '<p><span style="font-family: arial;">Το ΜΤΠΥ χορηγεί μέρισμα με εφαρμογή των διατάξεων του Ν.Δ. 4202/61 όπως τροποποιήθηκαν και συμπληρώθηκαν με το Ν. 1405/83 σύμφωνα με τις προϋποθέσεις που προβλέπει η νομοθεσία του.(ΠΔ 422/81 άρθρ. 40, άρθρο 14 του Ν. 1902/90 και την ΥΑ 7953/37/88, όπως αντικαταστάθηκε με το Ν. 2592/98 και στη συνέχεια με το Ν. 3336/05). </span></p>\r\n<p><span style="font-family: arial;">Σύμφωνα με τις ανωτέρω διατάξεις, ο μέτοχος του Ταμείου κατά την ημερομηνία της συνταξιοδότησής του και κατάθεσης του ΔΑΥΚ έχει το δικαίωμα να δηλώσει ότι επιθυμεί να μεταφέρει το χρόνο ασφάλισής του που διανύθηκε σε προηγούμενο επικουρικό Ταμείο (πχ ΤΕΑΜ, ΤΕΑΗΕ κλπ) ώστε να συνυπολογιστεί στη λοιπή συμμετοχή του. </span><span style="font-family: arial;">Το ΜΤΠΥ, ως ο τελευταίος ασφαλιστικός φορέας, εξετάζει το συνταξιοδοτικό δικαίωμα αποστέλλοντας έγγραφο στα συγκεκριμένα επικουρικά Ταμεία, με το οποίο ζητά την έκδοση απόφασης ή βεβαίωσης στην οποία να φαίνεται ο ακριβής αριθμός ημερομισθίων που διανύθηκαν σε αυτό προκειμένου να κρίνει το συνταξιοδοτικό δικαίωμα κανονισμού μερίσματος, συνυπολογίζοντας και το χρόνο αυτό (δηλαδή το χρόνο της διαδοχικής ασφάλισης των άλλων Επικουρικών Ταμείων). </span></p>\r\n<p><span style="font-family: arial;">Στη συνέχεια, μετά την αποστολή του χρόνου διαδοχικής ασφάλισης από το αντίστοιχο επικουρικό Ταμείο και εάν έχει πραγματοποιήσει 1500 ημερομίσθια στην ασφάλιση του ΜΤΠΥ και πληροί τις προϋποθέσεις της νομοθεσίας του Ταμείου (ΠΔ 422/81), κρίνει το συνταξιοδοτικό δικαίωμα και χορηγεί το μέρισμα συνυπολογιζομένου και του χρόνου διαδοχικής ασφάλισης του άλλου Επικουρικού Ταμείου. </span><span style="font-family: arial;">Στην περίπτωση που δεν έχει πραγματοποιήσει στην ασφάλιση του ΜΤΠΥ 1500 ημερομίσθια ή δεν πληρούνται οι προϋποθέσεις του ΠΔ 422/81, το ΜΤΠΥ διαβιβάζει το αίτημα για κρίση του συνταξιοδοτικού δικαιώματος στον προηγούμενο ασφαλιστικό φορέα κατά φθίνουσα σειρά αριθμών ημερομισθίων ή ετών ασφάλισης. </span><span style="font-family: arial;">Εφόσον μετά τη διαβίβαση της υπόθεσης δεν πληρούνται οι απαιτούμενες κατά τη νομοθεσία τους χρονικές (χρόνος ασφάλισης) και ηλικιακές (απαιτούμενο όριο ηλικίας) προϋποθέσεις για τη χορήγηση επικουρικής σύνταξης, τότε το αίτημα συνταξιοδότησης επανέρχεται στο τελευταίο Ταμείο (π.χ. το ΜΤΠΥ) για επανεξέταση του συνταξιοδοτικού δικαιώματος. <br /></span><span style="font-family: arial;">Ο διαδοχικός χρόνος δεν διασπάται και συνυπολογίζεται ολόκληρος, ως χρόνος που διανύθηκε στην ασφάλιση του ΜΤΠΥ για τον υπολογισμό του μερίσματος. <br /></span><span style="font-family: arial;">Μετά την ολοκλήρωση της διαδικασίας και εφόσον το ΜΤΠΥ θα χορηγήσει μέρισμα, εκδίδει: </span></p>\r\n<p><ol>\r\n<li><span style="font-family: arial;">απόφαση ΔΣ χορήγησης μερίσματος - σύνταξης με συνυπολογισμό όλου του μεταφερόμενου χρόνου των άλλων ασφαλιστικών Ταμείων, </span></li>\r\n<li><span style="font-family: arial;">απόφαση προσδιορισμού του ποσού της δαπάνης το οποίο υποχρεούνται να καταβάλλουν τα συμμετέχοντα Ταμεία στο απονέμων τη σύνταξη Ταμείο.</span></li>\r\n</ol></p>\r\n<p><span style="font-family: arial;">Στην περίπτωση που το ΜΤΠΥ καθίσταται αναρμόδιο να χορηγήσει μέρισμα, εκδίδεται απορριπτική απόφαση του Δ.Σ. με παράλληλη διαβίβαση του συνταξιοδοτικού φακέλου σε άλλο επικουρικό Ταμείο. <br /></span><span style="font-family: arial;">Αν μερισματούχος του ΜΤΠΥ ασφαλιστεί σε ομοειδές Ταμείο ή Οργανισμό του Ιδιωτικού τομέα μετά τη διακοπή της ασφάλισής του σ'' αυτό, έχει δικαίωμα να ζητήσει με αίτησή του ο χρόνος αυτός, υπό ορισμένες προϋποθέσεις, να συνυπολογιστεί στη λοιπή ασφάλισή του με προσαύξηση στο μηνιαίο του μέρισμα. <br /></span><span style="font-family: arial;">Τέλος, οι προϋποθέσεις για θεμελίωση ορφανικού δικαιώματος με εφαρμογή της διαδοχικής ασφάλισης, είναι όμοιες με εκείνες που απαιτούνται στην περίπτωση που ο μέτοχος έχει χρόνο ασφάλισης μόνο στο ΜΤΠΥ.</span><span style="font-family: arial;"> </span></p>', 1, 9, 0, 37, '2014-01-22 10:18:07', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 10:18:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 2, 'ελληνικό συνταξιοδοτικό διαδοχικής ασφάλισης\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'ελληνικό συνταξιοδοτικό διαδοχικής ασφάλισης', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(90, 'Ευρωπαϊκό  αναλογιστικό  ', 'europaiko-analogistiko', '<p><span style="font-family: arial;">Ο υπολογισμός του ευρωπαϊκού αναλογιστικού γίνεται ως εξής:</span></p>', '<p><span style="font-family: arial;">Το αναλογιστικό ισοδύναμο που μεταφέρεται στην περίπτωση επικουρικής ασφάλισης, είναι το γινόμενο του συντελεστή του αναλογιστικού ισοδύναμου επί το ποσό του μηνιαίου μερίσματος που θα εδικαιούτο ο αιτών κατά την ημερομηνία περιέλευσής της αίτησης του στον Οργανισμό Σύνδεσης, ανεξάρτητα από την συμπλήρωση των χρονικών προϋποθέσεων κανονισμού του μερίσματος, επί του αριθμού των ετησίως καταβαλλομένων μερισμάτων(14 μερίσματα). </span></p>\r\n<p><span style="font-family: arial;">Ο συντελεστής του αναλογιστικού ισοδυνάμου προσδιορίζεται από τον συνδυασμό της ηλικίας του υπαλλήλου κατά τον χρόνο υποβολής της αίτησής του στη Διοίκηση των Ευρωπαϊκών Κοινοτήτων και της ηλικίας έναρξης πληρωμής της σύνταξης. Οι διατάξεις αυτές έχουν εφαρμογή και για τους ασφαλισμένους οι οποίοι είχαν ασφαλιστεί σε περισσοτέρους του ενός φορείς επικουρικής ασφάλισης (Διαδοχική ασφάλιση). Το ίδιο δικαίωμα μπορούν να ασκήσουν και όσοι συνταξιοδοτήθηκαν από 1/1/62 μέχρι της έναρξης ισχύος του Ν. 2552/98. </span></p>\r\n<p><span style="font-family: arial;">Στην περίπτωση αυτή διακόπτεται το μέρισμα που λαμβάνουν από το ΜΤΠΥ και το αναλογιστικό ισοδύναμο που αντιστοιχεί στα εφεξής οφειλόμενα στο ΜΤΠΥ μεταφέρεται στην Διοίκηση των Ευρωπαϊκών Κοινοτήτων. </span><span style="font-family: arial;">Το διάστημα μεταφοράς του αναλογιστικού ισοδυνάμου δύνανται ν'' ασκήσουν οι δικαιούχοι μερίσματος λόγω θανάτου του μερισματούχου. Εάν οι δικαιούχοι είναι περισσότεροι του ενός, η αίτηση μεταφοράς γίνεται δεκτή μόνον αν υποβληθεί ταυτόχρονα από όλους. </span></p>\r\n<p><span style="font-family: arial;">Πριν ν'' αποφασίσουν οριστικά την μεταφορά των συνταξιοδοτικών Δικαιωμάτων (αφού έχουν λάβει την πρόταση της Διοίκησης) εφίσταται η προσοχή των ενδιαφερομένων στα ακόλουθα σημεία: <br /><br /></span></p>\r\n<p><ol>\r\n<li><span style="font-family: arial;"> Η αύξηση των συνταξίμων αυτών που προκύπτει από την μεταφορά των συνταξιοδοτικών δικαιωμάτων δεν λαμβάνεται υπόψη για τον υπολογισμό της δεκαετούς πραγματικής υπηρεσίας που απαιτείται για την θεμελίωση του δικαιώματος κοινοτικής σύνταξης.</span></li>\r\n<li><span style="font-family: arial;">Μόνον τα συνταξιοδοτικά δικαιώματα που αποκτήθηκαν πριν από την είσοδο στην υπηρεσία των Ευρωπαϊκών Κοινοτήτων μπορούν ν'' αποτελέσουν το αντικείμενο μεταφοράς καθώς και τ'' αποκτηθέντα ενδεχομένως κατά τη διάρκεια αδείας για προσωπικούς λόγους ή αποσπάσεις.</span></li>\r\n<li><span style="font-family: arial;">Η μεταφορά συνεπάγεται την απώλεια των συνταξιοδοτικών δικαιωμάτων που αποκτώνται βάσει των μεταφερόμενων περιόδων, την οποία θα μπορούσαν να έχουν γεννηθεί στα διάφορα εθνικά συνταξιοδοτικά συστήματα.</span></li>\r\n<li><span style="font-family: arial;">Όσον αφορά, ενδεχόμενη μεταφορά Συνταξιοδοτικού Δικαιώματος χηρών και ορφανών επειδή από Ευρωπαϊκών Κοινοτήτων εξασφαλίζεται ελάχιστο ποσοστό κοινοτικής Σύνταξης πιθανόν να μην είναι συμφέρουσα οπότε οι εν λόγω κατηγορίες προσώπων είναι απαραίτητο ν'' επικοινωνήσουν με την Κοινοτική Διοίκηση πριν να λάβουν οριστική απόφαση για την μεταφορά του συνταξιοδοτικού δικαιώματος. </span><span style="font-family: arial;"> </span></li>\r\n</ol></p>', 1, 9, 0, 37, '2014-01-22 10:29:55', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 10:29:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'Υπολογισμός ευρωπαϊκού αναλογιστικού\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Υπολογισμός ευρωπαϊκού αναλογιστικού', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(91, 'Προϋποθέσεις', 'proupotheseis', '<p>Οι προϋποθέσεις που απαιτούνται για την απόκτηση δικαιώματος μερίσματος:</p>', '<p><span style="font-family: arial;">Τα μέλη της οικογένειας μετόχου, που πέθανε στην Υπηρεσία θεμελιώνουν δικαίωμα για μέρισμα αν: </span></p>\r\n<p><ol>\r\n<li><span style="font-family: arial;"> ο μέτοχος έχει κατά την ημέρα του θανάτου του 15 χρόνια συμμετοχής στην ασφάλιση του Ταμείου.</span></li>\r\n<li><span style="font-family: arial;">ο μέτοχος έχει κατά την ημέρα του θανάτου του , τουλάχιστον, 10 χρόνια συμμετοχής κι εξαγοράσουν τα υπόλοιπα μέχρι την συμπλήρωση 15ετίας.</span></li>\r\n<li><span style="font-family: arial;">ο θάνατος οφείλεται στην Υπηρεσία κι εξαγοράσουν μέχρι την 15ετία, ανεξάρτητα από τον χρόνο συμμετοχής που έχει ο θανών στο Ταμείο. </span></li>\r\n</ol><span style="font-family: arial;">ΓΙΑ ΕΓΓΑΜΟΥΣ:</span></p>\r\n<p>\r\n<ul>\r\n<li><span style="font-family: arial;">Δικαιούχοι είναι ο χήρος ή η χήρα, οι ορφανές άγαμες κόρες, ανεξαρτήτου ηλικίας, και τα άγαμα αγόρια μέχρι την ηλικία των 18, εκτός και εφόσον σπουδάζουν σε Ανώτερα ή Ανώτατα εκπαιδευτικά ιδρύματα της ημεδαπής ή ισότιμα της αλλοδαπής. Οι φοιτητές δικαιούνται μερίσματος μέχρι την συμπλήρωση του 25ου έτους της ηλικίας τους. Εάν όμως αποφοιτήσουν νωρίτερα, η ημερομηνία διακοπής συμπίπτει με την ημερομηνία αποφοίτησης. Επίσης, δικαιούχοι είναι και τα άγαμα αγόρια που τυγχάνουν ανίκανα προς εργασία λόγω αναπηρίας. Το δικαίωμα τους δε για καταβολή μερίσματος κρίνεται μόνο από το Γενικό Λογιστήριο του Κράτους με Πράξη Συνταξιοδότησης. </span></li>\r\n</ul>\r\n</p>\r\n<p><span style="font-family: arial;">ΓΙΑ ΑΓΑΜΟΥΣ, ΔΙΑΖΕΥΓΜΕΝΟΥΣ Η ΧΗΡΕΣ/ΟΥΣ ΧΩΡΙΣ ΤΕΚΝΑ:</span></p>\r\n<p>\r\n<ul>\r\n<li><span style="font-family: arial;">Δικαιούχοι είναι οι γονείς. Σε περίπτωση που ο πατέρας δεν βρίσκεται εν ζωή, δικαιούχοι είναι οι τυχόν άγαμες αδελφές. </span></li>\r\n</ul>\r\n</p>\r\n<p><br style="font-family: arial;" /><span style="font-family: arial;"> </span></p>', 1, 9, 0, 38, '2014-01-22 12:25:10', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 12:25:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 5, 'Προϋποθέσεις μερίσματος θανόντος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Προϋποθέσεις μερίσματος θανόντος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(92, 'Δικαιολογητικά', 'dikaiologetika', '<p><span style="font-family: arial;">Τα δικαιολογητικά για το δικαίωμα απόκτησης μερίσματος σε μια οικογένεια θανόντος πρέπει να προσκομισθούν από την υπηρεσία του θανόντος:</span></p>', '<p>\r\n<ul>\r\n<li><span style="font-family: arial;">ΔΑΥΚ,</span></li>\r\n<li><span style="font-family: arial;">Αίτηση του/της ενδιαφερομένου/νης δικαιούχου ή δικαιούχων. (Μπορεί να συμπληρωθεί το ίδιο έντυπο που χρησιμοποιείται για την αίτηση ατομικού μερίσματος: http://www.mof-glk.gr/syntaxeis/entypa.htm, τρίτο από το τέλος),</span></li>\r\n<li><span style="font-family: arial;">Υπεύθυνες δηλώσεις (Ν. 1599/1986) από τους δικαιούχους στις οποίες θα δηλώνουν τα εξής: «Παραμένω χήρος/α ή άγαμος/η , είμαι δημότης του δήμου ………………., έχω Α.Φ.Μ ……………………..και δεν εισπράττω άλλο μέρισμα από το Μ.Τ.Π.Υ. (σε περίπτωση που εισπράττουν θα το δηλώσουν και θα αναφέρουν τον αριθμό μητρώου τους στο Μετοχικό Ταμείο. Σε περίπτωση δε, που είναι δημόσιοι υπάλληλοι με κρατήσεις υπέρ Μ.Τ.Π.Υ. θα δηλώσουν ότι είναι μέτοχοι του Μ.Τ.Π.Υ.) Στην ίδια υπεύθυνη δήλωση μπορούν, εάν το επιθυμούν, να δηλώσουν το εξής: «Εάν δεν πληρούνται οι προϋποθέσεις για απονομή μερίσματος παρακαλώ για την καταβολή του Εφάπαξ Βοηθήματος που μου αναλογεί»,</span></li>\r\n<li><span style="font-family: arial;">Φωτοτυπία των δύο όψεων των ταυτοτήτων όλων των δικαιούχων,</span></li>\r\n<li><span style="font-family: arial;">Πιστοποιητικό σπουδών για τα ορφανά αγόρια που δικαιούνται μέρισμα λόγω σπουδών (σε περίπτωση που οι σπουδές πραγματοποιούνται σε πανεπιστήμιο του εξωτερικού αυτό πρέπει να είναι αναγνωρισμένο και να βεβαιώνεται επίσης ότι ο τόπος διεξαγωγής των σπουδών είναι το ίδιο το πανεπιστήμιο που εκδίδει την βεβαίωση και ότι αυτές δεν πραγματοποιούνται μέσω τρίτου εκπαιδευτικού ιδρύματος),</span></li>\r\n<li><span style="font-family: arial;">Απλό φωτοαντίγραφο τραπεζικού λογαριασμού, στον οποίο ο δικαιούχος να είναι πρώτο όνομα κα να αναγράφεται ευκρινώς ο αριθμός ΙΒΑΝ. Αν υπάρχουν και άλλοι δικαιούχοι μπορούν να είναι συνδικαιούχοι στον ίδιο λογαριασμό ή να καταθέσουν φωτοαντίγραφο άλλων τραπεζικών λογαριασμών με πρώτο το δικό τους όνομα,</span></li>\r\n<li><span style="font-family: arial;">Πιστοποιητικό Οικογενειακής κατάστασης ή Εγγυτέρων Συγγενών.</span></li>\r\n</ul>\r\n<span style="font-family: arial;"><br /> </span></p>', 1, 9, 0, 38, '2014-01-22 12:30:38', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 12:30:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 4, 'Δικαιολογητικά μερίσματος θανόντος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Δικαιολογητικά μερίσματος θανόντος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(93, 'Ποσό μερίσματος', 'poso-merismatos', '<p><span style="font-family: arial;">Το μέρισμα καταβάλλεται στον τραπεζικό του λογαριασμού κάθε δικαιούχου μέλους της ορφανικής οικογένειας, κατά το μερίδιο που του αντιστοιχεί και σύμφωνα με τα παρακάτω:</span></p>', '<p>\r\n<ul>\r\n<li><span style="font-family: arial;">Η χήρα ή ο χήρος σύζυγος ή ένα ορφανό παιδί λαμβάνει το 1/2 του μερίσματος που ελάμβανε ο θανών.</span></li>\r\n<li><span style="font-family: arial;">Η χήρα ή ο χήρος σύζυγος με ένα παιδί δικαιούνται και οι δύο μαζί τα 5/8 του μερίσματος που θα ελάμβανε ο θανών.</span></li>\r\n<li><span style="font-family: arial;">Η χήρα ή ο χήρος σύζυγος με δύο παιδιά και άνω δικαιούνται όλοι μαζί τα 3/4 του μερίσματος που θα ελάμβανε ο θανών.</span></li>\r\n<li><span style="font-family: arial;">Οι γονείς ή οσαδήποτε αδέλφια άγαμου μετόχου δικαιούνται το 1/2 του μερίσματος του θανόντος</span></li>\r\n<li><span style="font-family: arial;">Δύο ορφανά παιδιά ή περισσότερα δικαιούνται τα 3/4 του μερίσματος του θανόντος.</span></li>\r\n<li><span style="font-family: arial;">Το μέρισμα ανήκει κατά ίσα μερίδια σε καθένα από τα μέλη της ορφανικής οικογένειας.</span></li>\r\n<li><span style="font-family: arial;">Δεν μπορεί να εισπράττει το ίδιο ή ίδια πρόσωπα περισσότερα από δύο μερίσματα.</span></li>\r\n</ul>\r\n</p>', 1, 9, 0, 38, '2014-01-22 12:33:39', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 12:33:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 3, 'Ποσό μερίσματος θανόντος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Ποσό μερίσματος θανόντος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(94, 'Έναρξη Διακοπή', 'enarxe-diakope', '<p><span style="font-family: arial;">Το δικαίωμα για μέρισμα αρχίζει:</span></p>', '<p><span style="font-family: arial;">Για τα δικαιούχα μέλη της οικογένειας του μετόχου που πέθανε στην Υπηρεσία, από την επομένη της λήξης των τριμήνων αποδοχών ή από την επομένη του θανάτου του, όταν ο Μέτοχος πεθάνει, κατά την διάρκεια των Τρίμηνων αποδοχών. </span></p>', 1, 9, 0, 38, '2014-01-22 12:37:09', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 12:37:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 2, 'Έναρξη Διακοπή μερίσματος θανόντος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Έναρξη Διακοπή μερίσματος θανόντος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(95, 'Αναθεώρηση', 'anatheorese', NULL, '<p><span style="font-family: arial;">Οι αποφάσεις του Διοικητικού Συμβουλίου του Μ.Τ.Π.Υ. γι΄ απονομή μερίσματος υπόκεινται σε αναθεώρηση, με αίτηση προς το Διοικητικό Συμβούλιο, εντός ενός έτους από την ημερομηνία κοινοποίησής τους (αρθρ. 54 του Π.Δ. 422/1981).</span></p>', 1, 9, 0, 38, '2014-01-22 12:39:24', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 12:39:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'Αναθεώρηση μερίσματος θανόντος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Αναθεώρηση μερίσματος θανόντος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(96, 'Υπέρ καταθέσεων', 'uper-katatheseon', NULL, '<p><span style="font-family: arial;">1. Επιστροφή στο ΔΙΚΑΙΟΥΧΟ που μισθοδοτήθηκε χωρίς να το δικαιούται και επέστρεψε στο Δημόσιο τις ακαθάριστες αποδοχές που έλαβε:</span></p>\r\n<p>\r\n<ul>\r\n<li><span style="font-family: arial;">Αίτηση επιστροφής του ενδιαφερόμενου όπου να αναγράφονται τα πλήρη προσωπικά και υπηρεσιακά του στοιχεία και ΑΦΜ.</span></li>\r\n</ul>\r\n</p>\r\n<p>\r\n<ul>\r\n<li><span style="font-family: arial;">Αναλυτική Βεβαίωση αποδοχών και καταθέσεων για το χρονικό διάστημα που ζητείται επιστροφή καταθέσεων, στην οποία να αναφέρεται η ημερομηνία έναρξης διενέργειας των υπέρ Μ.Τ.Π.Υ. καταθέσεων και του Δικαιώματος Εγγραφής, η αιτία της απόδοσης υπέρ Μ.Τ.Π.Υ. καταθέσεων χωρίς αυτές να οφείλονται και τα ποσά αυτών.</span></li>\r\n</ul>\r\n</p>\r\n<p>\r\n<ul>\r\n<li><span style="font-family: arial;">Αντίγραφο του διπλότυπου κατάθεσης των ακαθάριστων αποδοχών στη Δ.Ο.Υ. (ή άλλα παραστατικά απόδοσης).</span></li>\r\n</ul>\r\n</p>\r\n<p>\r\n<ul>\r\n<li><span style="font-family: arial;">Μισθοδοτικές καταστάσεις για το όλο χρονικό διάστημα που αφορά η επιστροφή καταθέσεων (υπογεγραμμένες αρμοδίως). Σε περίπτωση που ο μισθοδοτούμενος εισέπραξε αποδοχές από δύο υπηρεσίες (λόγω απόσπασης, μετάταξης κλπ.) υποβάλλονται οι καταστάσεις μισθοδοσίας και από τις δύο υπηρεσίες.</span></li>\r\n</ul>\r\n</p>\r\n<p>\r\n<ul>\r\n<li><span style="font-family: arial;">Φωτοτυπία της πρώτης σελίδας βιβλιαρίου της Τράπεζας (εκτός: Τράπεζας της Ελλάδος, Λαϊκής Τράπεζας, American Express, Παγκρήτιας Συνεταιριστικής Τράπεζας, Ταχυδρομικού Ταμιευτηρίου και Ταμείου Παρακαταθηκών και Δανείων) με πρώτο όνομα του δικαιούχου όπου να αναγράφεται ευκρινώς το ΙΒΑΝ. </span></li>\r\n</ul>\r\n<div><span style="font-family: arial;">2. Επιστροφή στο ΔΗΜΟΣΙΟ στην περίπτωση υπαλλήλου που εκ παραδρομής έλαβε μισθό χωρίς να το δικαιούται και επέστρεψε στο Δημόσιο το καθαρό ποσό:<br /></span></div>\r\n</p>\r\n<div>\r\n<ul>\r\n<li><span style="font-family: arial;">Αναλυτική Βεβαίωση αποδοχών και καταθέσεων για το χρονικό διάστημα που ζητείται επιστροφή καταθέσεων, στην οποία να αναγράφονται αναλυτικά τα προσωπικά και υπηρεσιακά στοιχεία του υπαλλήλου που αφορά η επιστροφή, η ημερομηνία έναρξης διενέργειας των υπέρ Μ.Τ.Π.Υ. καταθέσεων και του Δικαιώματος Εγγραφής, η αιτία της απόδοσης υπέρ Μ.Τ.Π.Υ. καταθέσεων χωρίς αυτές να οφείλονται και τα ποσά αυτών.</span></li>\r\n</ul>\r\n</div>\r\n<div><span style="font-family: arial;">\r\n<div>\r\n<ul>\r\n<li>Αντίγραφο του διπλότυπου κατάθεσης των καθαρών αποδοχών στη Δ.Ο.Υ. (ή άλλα παραστατικά απόδοσης).</li>\r\n</ul>\r\n</div>\r\n<ul>\r\n<li>Μισθοδοτικές καταστάσεις για το όλο χρονικό διάστημα που αφορά η επιστροφή κρατήσεων(υπογεγραμμένες αρμοδίως). Σε περίπτωση που ο μισθοδοτούμενος εισέπραξε αποδοχές από δύο υπηρεσίες (λόγω απόσπασης, μετάταξης κλπ.) υποβάλλονται οι καταστάσεις μισθοδοσίας και από τις δύο υπηρεσίες. </li>\r\n</ul>\r\n<div>3.Επιστροφή ποσών προς υπηρεσίες από εκ παραδρομής κατάθεση <br />\r\n<div>\r\n<ul>\r\n<li>Αναλυτικό έγγραφο για την εκ παραδρομής κατάθεση,</li>\r\n<li> Αναλυτικό παραστατικό απόδοσης (διπλότυπο κατάθεσης στη ΔΟΥ ή οποιοδήποτε άλλο παραστατικό απόδοσης)</li>\r\n</ul>\r\n</div>\r\nΌλα τα δικαιολογητικά πρέπει να είναι πρόσφατα.</div>\r\n</span></div>', 1, 9, 0, 39, '2014-01-22 01:07:29', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 13:07:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 3, 'δικαιολογητικά υπέρ καταθέσεων θανόντος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Δικαιολογητικά υπέρ καταθέσεων θανόντος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(97, 'Υπέρ κρατήσεων', 'uper-krateseon', '<p><span style="font-family: arial;">Σε μη μετόχους ή σε μετόχους με λάθος ποσοστά κράτησης εκ παραδομής</span></p>', '<p>\r\n<ul>\r\n<li><span style="font-family: arial;">Αίτηση επιστροφής του ενδιαφερόμενου όπου να αναγράφονται τα πλήρη προσωπικά και υπηρεσιακά του στοιχεία.</span></li>\r\n<li><span style="font-family: arial;">Αναλυτική Βεβαίωση αποδοχών και καταθέσεων για το χρονικό διάστημα που ζητείται επιστροφή καταθέσεων, στην οποία να αναφέρεται η αιτία της απόδοσης υπέρ Μ.Τ.Π.Υ. καταθέσεων χωρίς αυτές να οφείλονται και τα ποσά αυτών.</span></li>\r\n<li><span style="font-family: arial;">Αναλυτικό Πιστοποιητικό υπηρεσιακών μεταβολών στο οποίο να αναγράφονται τα έτη υπηρεσίας, το ΦΕΚ και η ημερομηνία διορισμού, η ημερομηνία έναρξης των υπέρ Μ.Τ.Π.Υ. καταθέσεων, καθώς και αν παρακρατήθηκε το δικαίωμα εγγραφής, η προϋπηρεσία και τα χρονικά διαστήματα αυτής και η σχέση εργασίας (δημοσίου, ιδιωτικού δικαίου κλπ)</span></li>\r\n<li><span style="font-family: arial;">Μισθοδοτικές καταστάσεις για το όλο χρονικό διάστημα που αφορά η επιστροφή κρατήσεων (υπογεγραμμένες αρμοδίως).</span></li>\r\n<li><span style="font-family: arial;">Φωτοτυπία της πρώτης σελίδας βιβλιαρίου της Τράπεζας (εκτός: Τράπεζας της Ελλάδος, Λαϊκής Τράπεζας, American Express, Παγκρήτιας Συνεταιριστικής Τράπεζας, Ταχυδρομικού Ταμιευτηρίου και Ταμείου Παρακαταθηκών και Δανείων) με πρώτο όνομα του δικαιούχου όπου να αναγράφεται ευκρινώς το ΙΒΑΝ.</span></li>\r\n</ul>\r\n</p>\r\n<p><span style="font-family: arial;"><strong>Ειδικά: για τα πρόσωπα που υπάγονται στις διατάξεις των άρθρ. 32 Ν2190/1994, άρθρ.6 Ν2703/1999, άρθρ.2 Ν.3075/2002 όπως π.χ. οι Πρόεδροι και τα Μέλη των Ανεξάρτητων Διοικητικών Αρχών, οι Πρόεδροι και Αναπληρωτές Γενικοί Διευθυντές των ΔΥΠΕ και οι Διοικητές και Αναπληρωτές Διοικητές των Νοσοκομείων των ΔΥΠΕ, ο Πρόεδρος του Δ.Σ. του Ο.Α.Σ.Π. κ.α. πρέπει να υποβληθεί και Βεβαίωση αποδοχών και καταθέσεων που θα διενεργούνταν αν οι ανωτέρω υπηρετούσαν για το εν λόγω χρονικό διάστημα στην οργανική τους θέση.</strong></span></p>\r\n<p><span style="font-family: arial;">*Ενδέχεται κατά περίπτωση και ύστερα από έλεγχο του φακέλου του ενδιαφερομένου να ζητηθούν εκ των υστέρων και άλλα δικαιολογητικά πέρα των ανωτέρω. </span></p>\r\n<p><span style="font-family: arial;"><span style="text-decoration: underline;">Όλα τα δικαιολογητικά πρέπει να είναι πρόσφατα.</span></span></p>', 1, 9, 0, 39, '2014-01-22 01:13:38', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 13:13:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 2, 'Δικαιολογητικά υπέρ κρατήσεων θανόντος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Δικαιολογητικά υπέρ κρατήσεων θανόντος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(98, 'Υπέρ αποδοχών αργίας', 'uper-apodokhon-argias', '<p><span style="font-family: arial;">Μετά την αυτοδίκαιη επαναφορά του υπαλλήλου στην υπηρεσία.</span></p>', '<p>\r\n<ul>\r\n<li><span style="font-family: arial;">Αίτηση επιστροφής του ενδιαφερόμενου όπου να αναγράφονται τα πλήρη προσωπικά και υπηρεσιακά του στοιχεία.</span></li>\r\n<li><span style="font-family: arial;">Απόφαση επιβολής της ποινής της αργίας,</span></li>\r\n<li><span style="font-family: arial;">Αμετάκλητη Αθωωτική Δικαστική Απόφαση,</span></li>\r\n<li><span style="font-family: arial;">Απόφαση Επαναφοράς στα υπηρεσιακά καθήκοντα,</span></li>\r\n<li><span style="font-family: arial;">Απόφαση αρμόδιου Υπηρεσιακού Συμβουλίου για την επιστροφή ή μη των παρακρατηθεισών αποδοχών,</span></li>\r\n<li><span style="font-family: arial;">Βεβαίωση της υπηρεσίας που να αναφέρει το χρονικό διάστημα της αργίας και τα ποσά των αποδοχών που αποδόθηκαν υπέρ Μ.Τ.Π.Υ. κατά το χρονικό διάστημα της αργίας,</span></li>\r\n<li><span style="font-family: arial;">Αναλυτικές μισθοδοτικές καταστάσεις για όλο το χρονικό διάστημα της αργίας όπου να φαίνονται οι υπέρ Μ.Τ.Π.Υ. καταθέσεις,</span></li>\r\n<li><span style="font-family: arial;">Φωτοτυπία της πρώτης σελίδας βιβλιαρίου της Τράπεζας (εκτός: Τράπεζας της Ελλάδος, Λαϊκής Τράπεζας, American Express, Παγκρήτιας Συνεταιριστικής Τράπεζας, Ταχυδρομικού Ταμιευτηρίου και Ταμείου Παρακαταθηκών και Δανείων) με πρώτο όνομα του δικαιούχου όπου να αναγράφεται ευκρινώς το ΙΒΑΝ.</span></li>\r\n</ul>\r\n</p>\r\n<p><span style="font-family: arial;"><strong>Ειδικά: Για τους υπαλλήλους του Υπουργείου Οικονομίας &amp; Οικονομικών και του Υπ. Εξωτερικών δεν απαιτείται απόφαση του Υπηρεσιακού Συμβουλίου παρά μόνο αμετάκλητη Αθωωτική Δικαστική Απόφαση. (Σε περιπτώσεις δωροδοκίας ή λαθρεμπορίας απαιτείται δεσμευτική – διαπιστωτική πράξη της Διοίκησης).</strong><br /><br />*Ενδέχεται κατά περίπτωση και ύστερα από έλεγχο του φακέλου του ενδιαφερομένου να ζητηθούν εκ των υστέρων και άλλα δικαιολογητικά πέρα των ανωτέρω. </span></p>\r\n<p><span style="font-family: arial;"><span style="text-decoration: underline;">Όλα τα δικαιολογητικά πρέπει να είναι πρόσφατα. </span></span></p>', 1, 9, 0, 39, '2014-01-22 01:18:09', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-22 13:18:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'δικαιολογητικά υπέρ αποδοχών αργίας\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Δικαιολογητικά υπέρ αποδοχών αργίας', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(99, 'Μερισματούχοι  εσωτερικού', 'merismatoukhoi-esoterikou', '<p><span style="font-family: arial;">Το Μ.Τ.Π.Υ. στέλνει ενημερωτικά σημειώματα για τα ποσά κατάθεσης στους μερισματούχους του ανά τρίμηνο.</span></p>\r\n<p></p>', '<p><span style="font-family: arial;">Οι πληρωμές μερισμάτων γίνονται από το Τμήμα Καταβολής Μερ/τος και Ηλεκτρονικών Πληρωμών, μέσω του διατραπεζικού συστήματος ΔΙΑΣ.</span></p>\r\n<p><span style="font-family: arial;">Τα μερίσματα καταβάλλονται μηνιαίως, τελευταία εργάσιμη ημέρα κάθε μήνα, μέσω του διατραπεζικού συστήματος ΔΙΑΣ στον τραπεζικό λογαριασμό που μας έχει δηλώσει ο κάθε μερισματούχος.</span></p>\r\n<p><span style="font-family: arial;">Σύμφωνα με τις διατάξεις της παραγράφου 13 του άρθρου 44 του Ν. 3986/11 από 1-9-2011 θεσπίστηκε Ειδική Εισφορά Συνταξιούχων Επικουρικής Ασφάλισης, η οποία υπολογίζεται μόνο για τα μερίσματα που υπερβαίνουν τα 300,00 € το μήνα. Η παρακράτηση γίνεται σε μηνιαία βάση και για ποσοστά που κλιμακώνονται από 3% μέχρι 10%, ανάλογα με το ύψος του μερίσματος. </span></p>\r\n<p><span style="font-family: arial;">Επίσης, σύμφωνα με τις διατάξεις της παραγράφου 4 του άρθρου 2 του Ν. 4024/11 από 1-11-2011 και εφεξής όλα τα καταβαλλόμενα μερίσματα του Μ.Τ.Π.Υ. μειώνονται κατά ποσοστό 20%, πλέον 50% για το τμήμα του μερίσματος που μετά την παρακράτηση της μείωσης του 20% υπερβαίνει τα 500 €.</span></p>\r\n<p><span style="font-family: arial;">Επιπροσθέτως, σύμφωνα με την Υποπερίπτωση Β3 του άρθρου 1 του Ν. 4093/2012, προβλέπεται και πρόσθετη μείωση του μηνιαίου μερίσματος. Όταν το συνολικό ποσό που προκύπτει από το άθροισμα των μηνιαίων συντάξεων και μερισμάτων του μερισματούχου είναι άνω των 1.000,00€ τότε προβλέπεται α) μείωση 5% από  1.000,01€ έως 1.500,00€, β) μείωση 10% από 1.500,01€ έως 2.000,00€ και γ) μείωση 15% από 2.000,00 και άνω.</span></p>\r\n<p><span style="font-family: arial;">Έτσι, το μέρισμα καταβάλλεται στο μερισματούχο μετά την παρακράτηση της Ειδικής Εισφοράς Συνταξιούχων Επικουρικής Ασφάλισης, της μείωσης του 20%, της μείωσης του Ν.4093/2012, καθώς και του αναλογούντος φόρου.</span></p>\r\n<p><span style="font-family: arial;">Για να γίνει κατάθεση του μερίσματος, ο μερισματούχος πρέπει να μας δηλώσει αριθμό τραπεζικού λογαριασμού και ΙΒΑΝ στον οποίο θα είναι πρώτος δικαιούχος. Κατά τη δήλωση του λογαριασμού, θα πρέπει να επισυνάπτει φωτοτυπία της πρώτης σελίδας του βιβλιαρίου του ή βεβαίωση της τράπεζας που εξέδωσε το λογαριασμό στην οποία να βεβαιώνει τον αριθμό του.</span></p>', 1, 10, 0, 40, '2014-01-23 07:45:11', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-23 07:45:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 4, 'μερισματούχοι εσωτερικού\r\nπληρωμές μερισμάτων\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'μερισματούχοι εσωτερικού', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(100, 'Μερισματούχοι  εξωτερικού', 'merismatoukhoi-exoterikou', NULL, '<p><span style="font-family: arial;">Οι πληρωμές μερισμάτων γίνονται από το Τμήμα Καταβολής Μερ/τος και Ηλεκτρονικών Πληρωμών, μέσω του διατραπεζικού συστήματος ΔΙΑΣ. </span><span style="font-family: arial;">Τα μερίσματα καταβάλλονται μηνιαίως, τελευταία εργάσιμη ημέρα κάθε μήνα, μέσω του διατραπεζικού συστήματος ΔΙΑΣ στον τραπεζικό λογαριασμό που μας έχει δηλώσει ο κάθε μερισματούχος.</span></p>\r\n<p><span style="font-family: arial;">Σύμφωνα με τις διατάξεις της παραγράφου 13 του άρθρου 44 του Ν. 3986/11 από 1-9-2011 θεσπίστηκε Ειδική Εισφορά Συνταξιούχων Επικουρικής Ασφάλισης, η οποία υπολογίζεται μόνο για τα μερίσματα που υπερβαίνουν τα 300,00 € το μήνα. Η παρακράτηση γίνεται σε μηνιαία βάση και για ποσοστά που κλιμακώνονται από 3% μέχρι 10%, ανάλογα με το ύψος του μερίσματος. </span><span style="font-family: arial;">Επίσης, σύμφωνα με τις διατάξεις της παραγράφου 4 του άρθρου 2 του Ν. 4024/11 από 1-11-2011 και εφεξής όλα τα καταβαλλόμενα μερίσματα του Μ.Τ.Π.Υ. μειώνονται κατά ποσοστό 20%, πλέον 50% για το τμήμα του μερίσματος που μετά την παρακράτηση της μείωσης του 20% υπερβαίνει τα 500 €.</span></p>\r\n<p><span style="font-family: arial;">Επιπροσθέτως, σύμφωνα με την Υποπερίπτωση Β3 του άρθρου 1 του Ν. 4093/2012, προβλέπεται και πρόσθετη μείωση του μηνιαίου μερίσματος. Όταν το συνολικό ποσό που προκύπτει από το άθροισμα των μηνιαίων συντάξεων και μερισμάτων του μερισματούχου είναι άνω των 1.000,00€ τότε προβλέπεται α) μείωση 5% από  1.000,01€ έως 1.500,00€, β) μείωση 10% από 1.500,01€ έως 2.000,00€ και γ) μείωση 15% από 2.000,00 και άνω. </span><span style="font-family: arial;">Έτσι, το μέρισμα καταβάλλεται στο μερισματούχο μετά την παρακράτηση της Ειδικής Εισφοράς Συνταξιούχων Επικουρικής Ασφάλισης, της μείωσης του 20%, της μείωσης του Ν.4093/2012, καθώς και του αναλογούντος φόρου. </span><span style="font-family: arial;">Για να γίνει κατάθεση του μερίσματος, ο μερισματούχος πρέπει να μας δηλώσει αριθμό τραπεζικού λογαριασμού και ΙΒΑΝ στον οποίο θα είναι πρώτος δικαιούχος. Κατά τη δήλωση του λογαριασμού, θα πρέπει να επισυνάπτει φωτοτυπία της πρώτης σελίδας του βιβλιαρίου του ή βεβαίωση της τράπεζας που εξέδωσε το λογαριασμό στην οποία να βεβαιώνει τον αριθμό του.</span></p>\r\n<p><span style="font-family: arial;"><br /><strong>Οι πληρωμές μερισμάτων στους μερισματούχους – κατοίκους του εξωτερικού, γίνονται από το Τμήμα Καταβολής Μερ/τος και Ηλεκτρονικών Πληρωμών στους τραπεζικούς λογαριασμούς της Ελλάδας που μας έχουν δηλώσει οι δικαιούχοι. Οι μερισματούχοι αυτοί έχουν την υποχρέωση να μας στέλνουν κάθε έξι μήνες (αρχές Δεκέμβρη και αρχές Ιούνη) βεβαίωση της αρμόδιας Προξενικής Αρχής για το αμετάβλητο της προσωπικής τους κατάστασης. </strong></span></p>', 1, 10, 0, 40, '2014-01-23 07:49:17', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-23 07:49:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 3, 'μερισματούχοι εξωτερικού\r\nπληρωμές μερισμάτων\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'μερισματούχοι  εξωτερικού', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(101, 'Αλλαγή διεύθυνσης', 'allage-dieuthunses', NULL, '<p><span style="font-family: arial;">Σε περίπτωση αλλαγής διεύθυνσης θα υποβάλετε στο αρμόδιο Τμήμα Εξυπηρέτησης Ασφαλισμένων (ιδιοχείρως, με FAX, ταχυδρομικά, μέσω Κ.Ε.Π. ή στο e-mail: service@mtpy.gr) Αίτηση-Υπεύθυνη Δήλωση, στην οποία θα αναγράφετε οπωσδήποτε τον αριθμό μητρώου σας στο Μ.Τ.Π.Υ., ονοματεπώνυμο, ΑΦΜ, ΑΔΤ, ΑΜΚΑ και θα δηλώνετε τη νέα διεύθυνση κατοικίας σας (οδό και αριθμό, περιοχή, Τ.Κ. και πόλη).</span></p>', 1, 10, 0, 40, '2014-01-23 07:51:32', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-23 07:51:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 2, 'αλλαγή διεύθυνσης\r\nπληρωμές μερισμάτων\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'αλλαγή διεύθυνσης', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(102, 'Αλλαγή τραπεζικού λογαριασμού', 'allage-trapezikou-logariasmou', NULL, '<p><span style="font-family: arial;">Σε περίπτωση αλλαγής τραπεζικού λογαριασμού, ο μερισματούχος οφείλει να ενημερώνει το Ταμείο με Αίτηση-Υπεύθυνη Δήλωσή του (ομοίως όπως και στην περίπτωση αλλαγής διεύθυνσης) επισυνάπτοντας συγχρόνως φωτοτυπία της πρώτης σελίδας του βιβλιαρίου. Στην περίπτωση αυτή εάν η ενημέρωση του Ταμείου γίνει ταχυδρομικά θα πρέπει η σχετική Αίτηση-Υπεύθυνη Δήλωση να είναι επικυρωμένη από Κ.Ε.Π. ως προς το γνήσιο της υπογραφής.</span></p>\r\n<p><span style="font-family: arial;">Για την αποφυγή άσκοπης ταλαιπωρίας, παρακαλείστε να μην «κλείνετε» τον προηγούμενο τραπεζικό σας λογαριασμό, εάν δε διαπιστώσετε πρώτα την πίστωση του νέου σας λογαριασμού με το ποσό του μερίσματος.</span></p>', 1, 10, 0, 40, '2014-01-23 07:53:25', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-23 07:53:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'αλλαγή τραπεζικού λογαριασμού\r\nπληρωμές μερισμάτων\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'αλλαγή τραπεζικού λογαριασμού', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(103, 'Αναστολή μερίσματος 2012', 'anastole-merismatos-2012', '<p><span style="font-family: arial;">Αναστολή χρηματικών διευκολύνσεων για την προεξόφληση μερίσματος έτους 2012.</span></p>', '<p><span style="font-family: arial;">Συνεχίστηκε  και το 2012, με απόφαση του ΔΣ, η αναστολή των χρηματικών διευκολύνσεων, επειδή τα διαθέσιμα κεφάλαια δεν επαρκούν για την καταβολή των μερισμάτων και  η προσπάθεια των αρμοδίων τμημάτων έχει επικεντρωθεί στη ρευστοποίηση των απαιτήσεων του Ταμείου.</span></p>', 1, 10, 0, 41, '2014-01-23 08:12:43', 62, NULL, '2014-01-23 08:24:24', 62, 0, '1979-12-19 00:00:00', '2014-01-23 08:12:00', '2060-01-01 00:00:00', NULL, 'Δείτε την σχετική απόφαση:|/mtpy/stoixeia_merismatouxoi/proex_merismatos/anast_merismatos.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 3, 0, 1, 'Αναστολή μερίσματος 2012\r\nΠροεξόφληση μερίσματος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Αναστολή μερίσματος 2012', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(104, 'Αναστολή σύνταξης 2012', 'anastole-suntaxes-2012', '<p><span style="font-family: arial;">Αναστολή χρηματικών διευκολύνσεων για την προεξόφληση σύνταξης έτους 2012.</span></p>', '<p><span style="font-family: arial;">Συνεχίστηκε και το 2012, με απόφαση του ΔΣ, η αναστολή των χρηματικών διευκολύνσεων επειδή τα διαθέσιμα κεφάλαια δεν επαρκούν για την καταβολή των μερισμάτων και η προσπάθεια των αρμοδίων τμημάτων έχει επικεντρωθεί στη ρευστοποίηση των απαιτήσεων του Ταμείου.</span></p>', 1, 10, 0, 42, '2014-01-23 08:29:16', 62, NULL, '2014-01-23 08:31:12', 62, 0, '1979-12-19 00:00:00', '2014-01-23 08:29:00', '2060-01-01 00:00:00', NULL, 'Δείτε την σχετική απόφαση:|/mtpy/stoixeia_merismatouxoi/proex_syntaxis/anast_merismatos.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 1, 'Αναστολή σύνταξης 2012\r\nπροεξόφληση σύνταξης\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Αναστολή σύνταξης 2012', 29, 3, 'greek');
INSERT INTO `elx_content` VALUES(105, 'Τμήμα  μερισμάτων', 'tmema-merismaton', '<p><span style="font-family: arial;"><strong>Ανακοίνωση</strong></span></p>', '<p><span style="font-family: arial;">Οι αποφάσεις του Διοικητικού Συμβουλίου του Μ.Τ.Π.Υ. γι΄ απονομή μερίσματος υπόκεινται σε αναθεώρηση, με αίτηση προς το Διοικητικό Συμβούλιο, εντός ενός έτους από την ημερομηνία κοινοποίησής τους (αρθρ. 54 του Π.Δ. 422/1981).<br /><br />Στοιχεία επικοινωνίας τμήματος κανονισμού μερισμάτων<br />τηλ.    2131500000<br />φαξ.   2103251510<br /> </span></p>', 1, 10, 0, 43, '2014-01-23 09:53:18', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-23 09:53:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'τμήμα κανονισμού μερισμάτων\r\nαναθεώρηση μερίσματος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'τμήμα κανονισμού μερισμάτων', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(106, 'Δικαιολογητικά', 'dikaiologetika', NULL, '<p><span style="font-family: arial;">Στην περίπτωση θανάτου του μερισματούχου, η οικογένεια του θανόντος θα πρέπει να προσκομίσει  στο Μ.Τ.Π.Υ.  ληξιαρχική πράξη θανάτου, προκειμένου να διακοπεί η καταβολή μερίσματος στον αποβιώσαντα. <br /><br /></span><span style="font-family: arial;">Ως οικογένεια στην οποία μεταβιβάζεται το δικαίωμα μερίσματος, νοείται:</span></p>\r\n<ul>\r\n<li><span style="font-family: arial;">Ο/Η σύζυγος, εφόσον ο γάμος τελέσθηκε, τουλάχιστον, προ εξαμήνου από την ημερομηνία του θανάτου.</span></li>\r\n<li><span style="font-family: arial;">Τα άγαμα κορίτσια, ανεξάρτητα αν εργάζονται και που </span></li>\r\n<li><span style="font-family: arial;">Τα άγαμα ανήλικα αγόρια ή στα άγαμα ενήλικα, εφ'' όσον είναι φοιτητές ΑΕΙ ή ΤΕΙ ημεδαπής ή αναγνωρισμένης αλλοδαπής, μέχρι την ηλικία των 25 ετών και για το βασικό πτυχίο. </span></li>\r\n<li><span style="font-family: arial;">Οι γονείς ή τα ορφανά από πατέρα αδέλφια του άγαμου μερισματούχου. Οι μεν αδελφές δικαιούνται, εφόσον είναι άγαμες, οι δε αδελφοί εφόσον πληρούν τις προϋποθέσεις της γ παραγράφου.</span></li>\r\n</ul>\r\n<p><span style="font-family: arial;"><span style="text-decoration: underline;">Δικαιολογητικά γ</span></span><span style="font-family: arial;"><span style="text-decoration: underline;">ια την μεταβίβαση του μερίσματος απαιτούνται </span>:</span></p>\r\n<ol>\r\n<li><span style="font-family: arial;"> </span><span style="font-family: arial;"><a title="Αίτηση μεταβίβασης μερίσματος" href="/mtpy/stoixeia_merismatouxoi/metab_merismatos/aithsh_metabibasis.doc" target="_blank">Αίτηση Μεταβίβασης Μερίσματος </a>στην οποία ο κάθε δικαιούχος συμπληρώνει όλα τα πεδία που τον αφορούν καθώς και τα στοιχεία που αφορούν τον θανόντα.</span></li>\r\n<li><span style="font-family: arial;">Υπεύθυνη Δήλωση του Ν. 1599/1986 με θεωρημένο το γνήσιο της υπογραφής, στην οποία ο κάθε δικαιούχος θα δηλώνει:</span></li>\r\n</ol>\r\n<p><span style="font-family: arial;"> </span></p>\r\n<ul>\r\n<li><span style="font-family: arial;">Την προσωπική του κατάσταση (π.χ. χήρα/χήρος, άγαμη ορφανή κόρη ‐ με την παρατήρηση ότι ουδέποτε τέλεσε γάμο‐, άγαμος &amp; ανήλικος ή φοιτητής ή ανίκανος προς εργασία λόγω αναπηρίας υιός). Τα τέκνα εναλλακτικά μπορούν να υποβάλουν πιστοποιητικό αγαμίας.</span></li>\r\n<li><span style="font-family: arial;">Εάν λαμβάνει άλλο μέρισμα από το ΜΤΠΥ, αναφέροντας τον αριθμό μητρώου του άλλου μερίσματος.</span></li>\r\n<li><span style="font-family: arial;"> εάν είναι δημόσιος υπάλληλος – μέτοχος του ΜΤΠΥ</span></li>\r\n<li><span style="font-family: arial;"> το Δήμο στον οποίο ανήκει η οικογενειακή του μερίδα,</span></li>\r\n<li><span style="font-family: arial;"> το ΑΦΜ και το ΑΜΚΑ. </span></li>\r\n</ul>\r\n<p><span style="font-family: arial;"> 3. Ληξιαρχική Πράξη Θανάτου του αποβιώσαντος μερισματούχου.<br /></span><span style="font-family: arial;"> 4. Φωτοτυπία των δύο όψεων της αστυνομικής του ταυτότητας ή φωτοτυπία διαβατηρίου.<br /></span><span style="font-family: arial;"> 5. Φωτοτυπία της πρώτης σελίδας βιβλιαρίου τραπεζικού λογαριασμού στην οποία να αναφέρεται ο αριθμός ΙΒΑΝ με πρώτο το όνομα του κάθε δικαιούχου στην περίπτωση κοινού τραπεζικού λογαριασμού.<br /></span><span style="font-family: arial;"> 6. Πιστοποιητικό πλησιέστερων συγγενών του αποβιώσαντος μερισματούχου στο οποίο θα αναφέρεται και η οικογενειακή κατάσταση αυτών.<br /></span><span style="font-family: arial;"> 7. Πιστοποιητικό/Βεβαίωση Σπουδών (στην περίπτωση άγαμου ορφανού φοιτητή υιού).<br /></span><span style="font-family: arial;"> 8. Πιστοποιητικό της ΑΣΥΕ στην περίπτωση ανάπηρης χήρας ή ανάπηρης άγαμης κόρης με ποσοστό αναπηρίας άνω του 80% προκειμένου το μέρισμά τους να είναι αφορολόγητο.<br /></span><span style="font-family: arial;"> 9. Πιστοποιητικό περί μη λύσεως γάμου, όπου απαιτείται (αν δεν αναφέρεται στο αντίστοιχο πιστοποιητικό η ημερομηνία τέλεσης του γάμου και ότι ο γάμος του θανόντος/θανούσης δεν είχε ποτέ λυθεί).<br /></span><span style="font-family: arial;"> 10. Αντίγραφο της Πράξης Μεταβίβασης Σύνταξης του Γενικού Λογιστηρίου του Κράτους απαιτείται για τη μεταβίβαση του μερίσματος μόνο στις εξής περιπτώσεις:<br /> </span></p>\r\n<ul>\r\n<li><span style="font-family: arial;">Όταν πρόκειται για ορφανό και ανίκανο υιό</span></li>\r\n<li><span style="font-family: arial;"> Όταν τα μέλη της ορφανικής οικογένειας (θάνατος στην υπηρεσία) ζητούν αναγνώριση χρόνου προϋπηρεσίας.</span></li>\r\n<li><span style="font-family: arial;">Όταν κατά την επεξεργασία της αίτησης μεταβίβασης διαπιστωθεί  ότι είναι αναγκαία η προσκόμιση της Πράξης Μεταβίβασης ΓΛΚ.</span></li>\r\n<li><span style="font-family: arial;"> Όταν η κύρια σύνταξη του αποβιώσαντος μερισματούχου προέρχεται από άλλο φορέα εκτός ΓΛΚ, η πράξη μεταβίβασης της σύνταξης αυτού του φορέα είναι αναγκαία.</span></li>\r\n</ul>\r\n<p></p>\r\n<p></p>\r\n<p><span style="font-family: arial;"> </span></p>', 1, 10, 0, 44, '2014-01-23 10:25:38', 62, NULL, '2014-01-23 10:48:29', 62, 0, '1979-12-19 00:00:00', '2014-01-23 10:25:00', '2060-01-01 00:00:00', NULL, 'Δείτε την σχετική αίτηση:|/mtpy/stoixeia_merismatouxoi/metab_merismatos/aithsh_metabibasis.doc\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 3, 0, 3, 'Δικαιολογητικά μεταβίβασης μερίσματος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Δικαιολογητικά μεταβίβασης μερίσματος', 29, 1, 'greek');
INSERT INTO `elx_content` VALUES(107, 'Ποσό μερίσματος', 'poso-merismatos', NULL, '<p><span style="font-family: arial;">Το μέρισμα καταβάλλεται στον τραπεζικό λογαριασμό κάθε δικαιούχου μέλους της ορφανικής οικογένειας, κατά το μερίδιο που του αντιστοιχεί και σύμφωνα με τα παρακάτω:<br /></span><span style="font-family: arial;"><br /><ol>\r\n<li>Η χήρα ή ο χήρος σύζυγος ή ένα ορφανό παιδί λαμβάνει τα 4/8 του μερίσματος που θα ελάμβανε ο θανών.</li>\r\n<li>Η χήρα ή ο χήρος σύζυγος με ένα παιδί δικαιούνται και οι δύο μαζί τα 5/8 του μερίσματος που θα ελάμβανε ο θανών.</li>\r\n<li>Η χήρα ή ο χήρος σύζυγος με δύο παιδιά και άνω δικαιούνται όλοι μαζί τα 6/8 του μερίσματος που θα ελάμβανε ο θανών.</li>\r\n<li>Οι γονείς ή οσαδήποτε αδέλφια άγαμου μετόχου δικαιούνται τα 4/8 του μερίσματος του θανόντος</li>\r\n<li>Δύο ορφανά παιδιά ή περισσότερα δικαιούνται τα 6/8 του μερίσματος του θανόντος.</li>\r\n<li>Το μέρισμα ανήκει κατά ίσα μερίδια σε καθένα από τα μέλη της ορφανικής οικογένειας.</li>\r\n<li>Δεν μπορεί να εισπράττει το ίδιο ή ίδια πρόσωπα περισσότερα από δύο μερίσματα.</li>\r\n</ol></span></p>', 1, 10, 0, 44, '2014-01-23 10:30:28', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-23 10:30:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 2, 'Ποσό μερίσματος\r\nμεταβίβασης μερίσματος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Ποσό μερίσματος', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(108, 'Έναρξη  Διακοπή', 'enarxe-diakope', NULL, '<p><span style="font-family: arial;">Στην Μεταβίβαση του μερίσματος, το δικαίωμα αρχίζει από την πρώτη μέρα της επόμενης τριμηνίας κατά την οποία συνέβη ο θάνατος του μερισματούχου </span></p>\r\n<p><span style="font-family: arial;"><strong>Διαγραφή Μέλους</strong></span></p>\r\n<p><span style="font-family: arial;">Το μέρισμα που μετεβιβάσθηκε διακόπτεται:</span></p>\r\n<p><ol>\r\n<li><span style="font-family: arial;">Αν η χήρα ή ο χήρος ή η άγαμη κόρη ή αδελφή παντρευτεί</span></li>\r\n<li><span style="font-family: arial;">Αν το ανήλικο αγόρι ενηλικιωθεί (18 ετών) ή περατώσει τις σπουδές του στο βασικό πτυχίο ή συμπληρώσει το 25ο έτος της ηλικίας του, ενώ εξακολουθεί να είναι φοιτητής και σε κάθε περίπτωση το μέρισμα δεν συνεχίζεται πέραν των 25 ετών.</span></li>\r\n<li><span style="font-family: arial;">Αν οποιοδήποτε μέλος αποβιώσει</span></li>\r\n</ol></p>\r\n<p><span style="font-family: arial;"><strong>Δικαιολογητικά Διαγραφής </strong></span></p>\r\n<p>\r\n<ul>\r\n<li><span style="font-family: arial; text-decoration: underline;">Για διαγραφή άρρενος παιδιού</span><span style="font-family: arial;">: </span></li>\r\n</ul>\r\n</p>\r\n<p><span style="font-family: arial;"> Απαραίτητη είναι η προσκόμιση φωτοτυπίας των δύο όψεων της ταυτότητάς του, καθώς επίσης (ανάλογα με τις κάτωθι αιτίες διαγραφής):</span></p>\r\n<p><ol>\r\n<li><span style="font-family: arial;">Για τη διαγραφή του λόγω συμπλήρωσης των 25 ετών της ηλικίας του, απαραίτητη είναι η προσκόμιση του πτυχίου του, στο οποίο να είναι εμφανής η ημερομηνία απόκτησής του, ενώ σε περίπτωση που εξακολουθεί να είναι φοιτητής, η προσκόμιση πρόσφατης βεβαίωσης σπουδών</span></li>\r\n<li><span style="font-family: arial;">Για τη διαγραφή του λόγω λήξης της φοιτητικής του ιδιότητας, απαραίτητη είναι η προσκόμιση του πτυχίου του, στο οποίο να είναι εμφανής η ημερομηνία απόκτησής του</span></li>\r\n<li><span style="font-family: arial;">Για τη διαγραφή του λόγω συμπλήρωσης των 18 ετών της ηλικίας του, εφόσον δεν έχει αποκτήσει τη φοιτητική ιδιότητα, αρκεί η προσκόμιση της φωτοτυπίας των δύο όψεων της ταυτότητάς του</span></li>\r\n</ol></p>\r\n<p><span style="font-family: arial;"><span style="text-decoration: underline;">Σημείωση</span>: Απαραίτητη προϋπόθεση προκειμένου τα άρρενα παιδιά να δικαιούνται μερίσματος είναι η αγαμία τους </span></p>\r\n<p>\r\n<ul>\r\n<li><span style="font-family: arial; text-decoration: underline;">Για διαγραφή λόγω γάμου</span><span style="font-family: arial;">: </span></li>\r\n</ul>\r\n</p>\r\n<p><span style="font-family: arial;"> Απαραίτητη είναι η προσκόμιση της ληξιαρχικής πράξης γάμου <br /><br /></span></p>\r\n<p>\r\n<ul>\r\n<li><span style="font-family: arial;"><span style="text-decoration: underline;">Για διαγραφή λόγω θανάτου: </span></span></li>\r\n</ul>\r\n</p>\r\n<p><span style="font-family: arial;"> Απαραίτητη είναι η προσκόμιση της ληξιαρχικής πράξης θανάτου </span></p>\r\n<p><span style="font-family: arial;">Για όλες τις ανωτέρω αιτίες διαγραφής, θα πρέπει επίσης να υποβληθούν:</span></p>\r\n<p><ol>\r\n<li><span style="font-family: arial;">Πιστοποιητικό οικογενειακής κατάστασης, στο οποίο να αναγράφεται η προσωπική κατάσταση των μελών, τα οποία θα εξακολουθήσουν να παίρνουν μέρισμα. Ειδικότερα, γαι τις κόρες που εξακολουθούν να δικαιούνται του μερίσματος ως άγαμες, εκτός από το πιστοποιητικό οικογενειακής κατάστασης θα πρέπει να συμπληρώσουν υπεύθυνη δήλωση του Ν. 1599/86, στην οποία να δηλώνουν ότι παραμένουν άγαμες.</span></li>\r\n<li><span style="font-family: arial;">Φωτοτυπία των δύο όψεων των αστυνομικών ταυτοτήτων των δικαιούχων</span></li>\r\n<li><span style="font-family: arial;">Έγγραφο από το οποίο να προκύπτουν οι Α.Φ.Μ. των δικαιούχων</span></li>\r\n<li><span style="font-family: arial;">Φωτοτυπία της πρώτης σελίδας του βιβλιαρίου της τράπεζας, στο οποίο όλα τα εναπομείναντα μέλη να είναι συνδικαιούχα, καθώς επίσης πρώτος δικαιούχος να είναι κάποιος που δικαιούται μερίσματος</span></li>\r\n</ol></p>\r\n<p><span style="font-family: arial;"><strong>Διακοπή</strong></span></p>\r\n<p><ol>\r\n<li><span style="font-family: arial;">Εάν διαγραφούν όλοι οι δικαιούχοι</span></li>\r\n<li><span style="font-family: arial;">Εάν ο μερισματούχος δεν αφήνει δικαιούχα πρόσωπα για μεταβίβαση μερίσματος</span></li>\r\n</ol></p>\r\n<p><span style="font-family: arial;">Το μέρισμα που δεν πληρώθηκε στον αποβιώσαντα μερισματούχο μεταβιβάζεται στους δικαιούχους μερίσματος. Αν δεν υπάρχουν όμως, μένει εις όφελος του Μ.Τ.Π.Υ. (Π.Δ. 422/1981) </span></p>\r\n<div></div>\r\n<p><span style="font-family: arial;"> </span></p>', 1, 10, 0, 44, '2014-01-23 10:42:52', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-23 10:42:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'Έναρξη διακοπή μεταβίβασης μερίσματος\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Έναρξη διακοπή μεταβίβασης μερίσματος', 29, 0, 'greek');
