INSERT INTO `elx_content` VALUES(111, 'Πόροι', 'poroi', '<p><span style="font-family: arial;">Το ποσοστό κράτησης των πόρων επί των δαπανών αναλυτικά.</span></p>', NULL, 1, 11, 0, 47, '2014-01-24 09:00:33', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-24 09:00:00', '2060-01-01 00:00:00', NULL, 'Δείτε αναλυτικά τους πίνακες:|/mtpy/stoixeia_krathsewn/dapanes/poroi.xls\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 2, 'Πόροι\r\nΚρατήσεις επί των δαπανών\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Κρατήσεις επί των δαπανών', 29, 0, 'greek');
