INSERT INTO `elx_content` VALUES(113, 'Συναλλαγή 4300', 'sunallage-4300', '<p><span style="font-family: arial;">Η συναλλαγή 4300 της Εθνικής τράπεζας της Ελλάδας για το ΜΤΠΥ.</span></p>', NULL, 1, 11, 0, 48, '2014-01-24 09:27:49', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-24 09:27:00', '2060-01-01 00:00:00', NULL, 'Δείτε την σχετική Εγκύκλιο|/mtpy/stoixeia_krathsewn/ete4300/ODHGIES.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 2, 'Εγκύκλιος συναλλαγής 4300 εθνική τράπεζα της Ελλάδας\r\nΚρατήσεις \r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Συναλλαγή 4300 ΕΤΕ', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(114, 'Έντυπο 4300', 'entupo-4300', '<p><span style="font-family: arial;">Το έντυπο της Εθνικής τράπεζας της Ελλάδας για την συναλλαγή 4300 υπερ ΜΤΠΥ.</span></p>', NULL, 1, 11, 0, 48, '2014-01-24 09:31:22', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-24 09:31:00', '2060-01-01 00:00:00', NULL, 'Δείτε το σχετικό έντυπο:|/mtpy/stoixeia_krathsewn/ete4300/ENTYPO.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'Έντυπο συναλλαγής 4300 \r\nΕθνική τράπεζα\r\nΚρατήσεις\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Έντυπο συναλλαγής 4300 ΕΤΕ', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(115, 'Γενικά', 'genika', NULL, '<p><span style="font-family: arial;">Το ΠΔ 422/1981 «Περί Κωδικοποιήσεως των περί Μετοχικού Ταμείου Πολιτικών Υπαλλήλων διατάξεων», άρθρα 21-31,</span></p>\r\n<p><ol>\r\n<li><span style="font-family: arial;">Τον Οδηγό Κρατήσεων υπέρ του Μ.Τ.Π.Υ.,</span></li>\r\n<li><span style="font-family: arial;">Το ΠΔ 94/2011 «Οργανισμός Μετοχικού Ταμείου Πολιτικών Υπαλλήλων», άρθρο 6, παρ. 3ζ΄,</span></li>\r\n<li><span style="font-family: arial;">Το Τμήμα Ελεγκτών του Μ.Τ.Π.Υ. διενεργεί επιτόπιους συμβουλευτικούς ελέγχους σε διάφορους φορείς του Δημοσίου, προκειμένου να διαπιστωθεί η ορθή εφαρμογή των διατάξεων του Ταμείου (ΠΔ 422/81), με την ορθή κράτηση και απόδοση των υπέρ Μ.Τ.Π.Υ. κρατήσεων.</span></li>\r\n</ol></p>', 1, 12, 0, 49, '2014-01-24 11:21:17', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-24 11:21:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 4, 'Γενικές Πληροφορίες\r\nΕλεγκτές Έλεγχοι\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Γενικές Πληροφορίες \r\nΈλεγχοι', 29, 1, 'greek');
INSERT INTO `elx_content` VALUES(116, 'Στόχοι', 'stokhoi', NULL, '<p><span style="font-family: arial;">Η επίτευξη του προορισμού του Ταμείου βασίζεται στην διασφάλιση και τον έλεγχο των εισροών και των εκροών του. </span><span style="font-family: arial;">Οι πόροι του Ταμείου προέρχονται, μεταξύ των άλλων, από τις καταθέσεις των μετόχων, από τους πόρους επί δαπανών των διαφόρων Υπουργείων και των Ν.Π.Δ.Δ.. Επισημαίνεται ότι ο Ν. 3863/2010 προβλέπει ότι ο υπολογισμός του μερίσματος θα είναι ανάλογος των καταθέσεων εκάστου μετόχου. Συνεπώς η μη παρακράτηση και απόδοση των καταθέσεων θα έχει ως αντίκτυπο στον υπολογισμό του μερίσματος, ενώ η διασφάλιση των εσόδων του Μ.Τ.Π.Υ., δίνει τα εχέγγυα για την πραγμάτωση του σκοπού του και την εξασφάλιση παροχής μερίσματος στους μετόχους-μερισματούχους, καθώς και στην ενίσχυση Κρατικού Προϋπολογισμού (χαρτόσημο και ΟΓΑ).</span></p>', 1, 12, 0, 49, '2014-01-24 11:23:32', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-24 11:23:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 3, 'Στόχοι Ελεγκτές έλεγχοι\r\nμετοχικό ταμείο πολιτικών υπαλλήλων\r\n', 'Στόχοι Ελεγκτές', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(117, 'Δράση', 'drase', '<p><span style="font-family: arial;">Η δράση των ελεκτών συνοψίζεται στα εξής:</span></p>', '<p><ol>\r\n<li><span style="font-family: arial;">Η διενέργεια επιτόπιων συμβουλευτικών ελέγχων στην έδρα της υπηρεσίας για την παρακολούθηση ορθής παρακράτησης και απόδοσης των καταθέσεων των μετόχων και πόρων του Ταμείου.</span></li>\r\n<li><span style="font-family: arial;">Σύνταξη Εκθέσεων με τα αποτελέσματα από τα ευρήματα των διενεργηθέντων ελέγχων καθώς και τις διαπιστώσεις - συμπεράσματα για την ορθή παρακράτηση και απόδοση ή όχι των νομίμων υπέρ ΜΤΠΥ κρατήσεων.</span></li>\r\n<li><span style="font-family: arial;">Σύνταξη ενημερωτικών εγκυκλίων για την επίλυση προβλημάτων σχετικά με την παρακράτηση και απόδοση των καταθέσεων των μετόχων και πόρων.</span></li>\r\n<li><span style="font-family: arial;">Παρακολούθηση και έλεγχος των εσόδων και πόρων του Ταμείου, σε συνεργασία με τα Τμήματα Παρακολούθησης Καταθέσεων Μετόχων και Παρακολούθησης Πόρων για καθυστερήσεις και προβλήματα που παρουσιάζονται στην απόδοση των καταθέσεων και κρατήσεων.</span></li>\r\n<li><span style="font-family: arial;">Ενημέρωση όλων των αρμόδιων ελεγκτικών Υπηρεσιών του Δημοσίου (ΥΔΕ, ΣΔΟΕ, ΣΟΛ ΑΕ, ΓΛΚ, Ελεγκτικό Συνέδριο, Υπουργός), με τις διαπιστώσεις και παραλείψεις, όπως προκύπτουν από τους ελέγχους.</span></li>\r\n</ol></p>', 1, 12, 0, 49, '2014-01-24 11:25:58', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-24 11:25:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 2, 'Δράση ελεγκτών έλεγχοι\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Δράση ελεγκτών', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(118, 'Ελεγχόμενες υπηρεσίες', 'elegkhomenes-uperesies', '<p><span style="font-family: arial;">Οι ελεγχόμενες υπηρεσίες συνοψιζονται στις παρακάτω:</span></p>', '<p><ol>\r\n<li><span style="font-family: arial;">Δημόσιες Υπηρεσίες (κεντρικές και περιφερειακές)</span></li>\r\n<li><span style="font-family: arial;">Ν.Π.Δ.Δ.</span></li>\r\n<li><span style="font-family: arial;">Ανεξάρτητες Διοικητικές Αρχές (Α.Δ.Α.)</span></li>\r\n<li><span style="font-family: arial;">Οργανισμοί Τοπικής Αυτοδιοίκησης Α΄ και Β΄ βαθμού</span></li>\r\n<li><span style="font-family: arial;">Κρατικά Νομικά Πρόσωπα Ιδιωτικού Δικαίου</span></li>\r\n<li><span style="font-family: arial;">Διαδικασία ελέγχου</span></li>\r\n</ol></p>\r\n<div><span style="font-family: arial;">Η ελεγκτική διαδικασία ενεργοποιείται με το ΠΔ 94/2011 «Οργανισμός Μετοχικού Ταμείου Πολιτικών Υπαλλήλων», αρ. 6 παρ. 3ζ΄.</span></div>\r\n<div>\r\n<div><span style="font-family: arial;">Ο έλεγχος διενεργείται στην έδρα της υπηρεσίας σε προκαθορισμένη ημερομηνία και κατόπιν σχετικής συνεννόησης με τους αρμόδιους προϊσταμένους, προκειμένου να τεθούν στη διάθεση των Ελεγκτών τα απαιτούμενα παραστατικά-δικαιολογητικά μισθοδοσίας και δαπανών.</span></div>\r\n<div><span style="font-family: arial;">Με την ολοκλήρωση του ελέγχου συντάσσεται Έκθεση, η οποία κοινοποιείται στην ελεγχόμενη Υπηρεσία και στο Ελεγκτικό Συνέδριο, με τις διαπιστώσεις και τις απαιτήσεις του Ταμείου από τα ευρήματα του ελέγχου, προκειμένου οι αρμόδιοι προϊστάμενοι του ελεγχόμενου φορέα να θέσουν τυχόν παρατηρήσεις ή ενστάσεις εντός μηνός από την κοινοποίησή της.</span></div>\r\n<div><span style="font-family: arial;"><br /></span></div>\r\n<div><span style="font-family: arial;">Μετά την πάροδο προθεσμίας ενός μηνός από την κοινοποίηση της Έκθεσης, το Τμήμα συντάσσει υπηρεσιακό σημείωμα, το οποίο υποβάλλεται στο Δ.Σ. προκειμένου να επικαιροποιηθεί και να διενεργηθούν οι σχετικοί καταλογισμοί. </span></div>\r\n</div>', 1, 12, 0, 49, '2014-01-24 11:29:55', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-24 11:29:00', '2060-01-01 00:00:00', NULL, NULL, 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'Ελεγχόμενες υπηρεσίες \r\nΕλεγκτές  έλεγχοι\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Ελεγχόμενες υπηρεσίες Ελεκτές', 29, 0, 'greek');
INSERT INTO `elx_content` VALUES(119, 'Έλεγχοι 2010 ', 'elegkhoi-2010', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει το αναλυτικό αρχείο με τους ελέγχους που διενεργήθηκαν από το ΜΤΠΥ κατά τα έτη 2010-2011.</span></p>', NULL, 1, 12, 0, 50, '2014-01-24 12:06:30', 62, NULL, '2014-01-24 12:11:40', 62, 0, '1979-12-19 00:00:00', '2014-01-24 12:06:00', '2060-01-01 00:00:00', NULL, 'Δείτε το σχετικό αρχείο:|/mtpy/stoixeia_elegxwn/drastiriothtes/elegxoi2010.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 4, 0, 2, 'Δραστηριότητες\r\nΕπιτόπιοι έλεγχοι\r\nΕλεγκτές\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Δραστηριότητες\r\nΕπιτόπιοι έλεγχοι', 29, 3, 'greek');
INSERT INTO `elx_content` VALUES(120, 'Έλεγχοι 2012', 'elegkhoi-2012', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει το αναλυτικό αρχείο με τους ελέγχους που διενεργήθηκαν από το ΜΤΠΥ κατά το Ιανουάριο 2012 μέχρι Ιούλιο 2013.</span></p>', NULL, 1, 12, 0, 50, '2014-01-24 12:14:52', 62, NULL, '2014-01-24 12:20:32', 62, 0, '1979-12-19 00:00:00', '2014-01-24 12:14:00', '2060-01-01 00:00:00', NULL, 'Δείτε το σχετικό αρχείο:|/mtpy/stoixeia_elegxwn/drastiriothtes/elegxoi2012.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 1, 'Δραστηριότητες\r\nΕπιτόπιοι έλεγχοι  Ελεγκτές\r\nμετοχικό ταμείο πολιτικών υπαλλήλων\r\n', 'Δραστηριότητες\r\nΕπιτόπιοι ελέγχοι', 29, 5, 'greek');
INSERT INTO `elx_content` VALUES(121, 'Επεξεργασία δεδομένων', 'epexergasia-dedomenon', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει πληροφορίες για την επεξεργασία δεδομένων του τμήματος ελεκτών του ΜΤΠΥ στο πλαίσιο ελέγχου μισθοδοσίας και παρακολούθησης δαπανών των φορέων του δημοσίου.</span></p>', NULL, 1, 12, 0, 51, '2014-01-24 02:15:46', 62, NULL, '1979-12-19 00:00:00', 0, 0, '1979-12-19 00:00:00', '2014-01-24 14:15:00', '2060-01-01 00:00:00', NULL, 'Δείτε την σχετική Εγκύκλιο|/mtpy/stoixeia_elegxwn/egkyklioi/dedomena.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 1, 0, 1, 'Εγκύκλιοι\r\nτμήματος ελεγκτών\r\nέλεγχοι\r\nμετοχικό ταμείο πολιτικών υπαλλήλων', 'Εγκύκλιοι τμήματος ελεγκτών', 29, 2, 'greek');
INSERT INTO `elx_content` VALUES(122, 'Πρυτάνεις', 'prutaneis', '<p><span style="font-family: arial;">Ο οδηγός κρατήσεων υπερ του ΜΤΠΥ για τους πρυτάνεις, αντιπρυτάνεις, κοσμήτορες και πρόεδρους του τμημάτων ΑΕΙ-ΤΕΙ.</span></p>', NULL, 1, 13, 0, 86, '2014-01-29 08:22:29', 62, NULL, '2014-01-29 08:25:35', 62, 0, '1979-12-19 00:00:00', '2014-01-29 08:22:00', '2060-01-01 00:00:00', NULL, 'Δείτε την σχετική απόφαση:|/mtpy/stoixeia_krathsewn/odhgoi/panepisthmia/prytaneis.pdf\n', 'pageclass_sfx=\ntextclass_sfx=\nback_button=\nitem_title=1\nlink_titles=\nintrotext=1\nsection=0\nsection_link=0\ncategory=0\ncategory_link=0\nrating=\nauthor=\ncreatedate=\nmodifydate=\ntranslate=2\naddthis=2\nemailit=2\npdf=\nrtf=\nprint=\nemail=\npost_comments=0\nkeyref=', 2, 0, 3, 'Πρυτάνεις, Πανεπιστήμια, Κρατήσεις', 'Πρυτάνεις Πανεπιστήμια Κρατήσεις', 29, 0, 'greek');
