INSERT INTO `elx_sections` VALUES(1, 'Σύντομα νέα', 'Σύντομα νέα', NULL, 'content', 'left', '<p>Αυτή η ενότητα και οι κατηγορίες της δεν συνδέονται με κανένα μενού και γιαυτό είναι άμεσα εμφανείς στο δημόσιο τμήμα του ιστότοπου. Τα αντικείμενα περιεχομένου αυτής της ενότητας και των κατηγοριών της χρησιμοποιούνται μόνο για την τροφοδοσία του module Newsflash. <u>Δείτε επίσης</u>, MODULES-&gt;MODULES ΙΣΤΟΤΟΠΟΥ-&gt;NEWSFLASH</p>', 0, 0, '1979-12-19 00:00:00', 12, 29, 0, NULL, NULL, 'newsflashes');
INSERT INTO `elx_sections` VALUES(2, 'Συχνές ερωτήσεις', 'Συχνές ερωτήσεις', 'pastarchives.jpg', 'content', 'left', '<p>Αυτή η ενότητα περιέχει απαντήσεις σε Συχνές Ερωτήσεις αναφορικά με το Elxis και τις λειτουργίες του.</p>', 0, 0, '1979-12-19 00:00:00', 13, 29, 0, NULL, NULL, 'faqs');
INSERT INTO `elx_sections` VALUES(3, 'Ανακαλύψτε το Elxis 2009', 'Ανακαλύψτε το Elxis 2009', NULL, 'content', 'left', '<p align="justify">Το Elxis εξελίχθηκε! Αυτή η ενότητα παρουσιάζει τα σημαντικότερα χαρακτηριστικά, προσθήκες και αλλαγές του Elxis 2009.</p>', 0, 0, '1979-12-19 00:00:00', 14, 29, 0, NULL, NULL, 'discover-elxis');
INSERT INTO `elx_sections` VALUES(4, 'Προκηρύξεις', 'Προκηρύξεις', 'metoxiko.jpg', 'content', 'left', '<p><span style="font-family: arial;">Περιλαμβάνονται όλες οι προκηρύξεις για το Μετοχικό Ταμείο Πολιτικών Υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', 11, 29, 0, NULL, 'greek', 'prokeruxeis');
INSERT INTO `elx_sections` VALUES(5, 'Ενημέρωση', 'Ενημέρωση', 'news.jpg', 'content', 'left', '<p><span style="font-family: arial;">Πύλη ενημέρωσης για το Μετοχικό Ταμείο Πολιτικών Υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', 10, 29, 0, NULL, 'greek', 'enemerose');
INSERT INTO `elx_sections` VALUES(6, 'Εφημερίδα  του  ΜΤΠΥ', 'Εφημερίδα  του  ΜΤΠΥ', 'mtpy_efimerida.jpg', 'content', 'left', '<p><span style="font-family: arial;">«Εφημερίδα της Υπηρεσίας» είναι μια πύλη στον Ιστοχώρο της Υπηρεσίας, στην οποία δημοσιεύονται τα διοικητικά έγγραφα που παράγει η Υπηρεσία, με προκαθορισμένο τρόπο και σύμφωνα με τους περιορισμούς και τις προϋποθέσεις που προβλέπονται από την κείμενη νομοθεσία για την προστασία συμφερόντων, δικαιωμάτων και εννόμων αγαθών.</span></p>\r\n<p><span style="font-family: arial;">Η υποχρέωση για τη δημιουργία της «Εφημερίδας της Υπηρεσίας» αφορά στα Υπουργεία, τις Ανεξάρτητες Αρχές, τις Περιφέρειες, τους Ο.Τ.Α. Α΄ και Β΄ βαθμού και τα Ν.Π.Δ.Δ.</span></p>', 1, 0, '1979-12-19 00:00:00', 9, 29, 0, NULL, 'greek', 'ephemerida');
INSERT INTO `elx_sections` VALUES(7, 'Οικονομικά', 'Οικονομικά', 'ekonomika.jpg', 'content', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιλαμβάνονται τα οικονομικά στοιχεία για το Μετοχικό Ταμείο Πολιτικών Υπαλλήλων όπως προϋπολογισμοί, ισολογισμοί, ετήσιες εκθέσεις και προγράμματα δράσεων.</span></p>', 1, 0, '1979-12-19 00:00:00', 8, 29, 0, NULL, 'greek', 'oikonomika');
INSERT INTO `elx_sections` VALUES(8, 'Ταμείο', 'Ταμείο', 'metoxiko.jpg', 'content', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιλαμβάνονται η ιστορία, η ταυτότητα, οι διευθύνσεις μαζί με το διοικητικό συμβούλιο καθώς και η νομική ιδιότητα του μετοχικού ταμείου πολιτικών υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', 7, 29, 0, NULL, 'greek', 'tameio');
INSERT INTO `elx_sections` VALUES(9, 'Μέτοχοι', 'Μέτοχοι', NULL, 'content', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιλαμβάνονται πληροφορίες για τους μετόχους, για τις χρηματικές διευκολύνσεις, για την αναγνώριση προϋπηρεσίας, για τον κανονισμό μερίσματος, για την διαδοχική ασφάλιση, για την μεταβίβαση μερίσματος λόγω θανάτου στην Υπηρεσία και για την επιστροφή αχρεωσθέντων καταβληθέντων ποσών του μετοχικού ταμείου πολιτικών υπαλλήλων.</span></p>', 1, 0, '1979-12-19 00:00:00', 6, 29, 0, NULL, 'greek', 'metokhoi');
INSERT INTO `elx_sections` VALUES(10, 'Μερισματούχοι', 'Μερισματούχοι', NULL, 'content', 'left', '<p><span style="font-family: arial;">Στην πύλη αυτή περιλαμβάνονται πληροφορίες για τους μερισματούχους. Πιο συγκεκριμένα περιγράφονται ο τρόπος πληρωμής των μερισμάτων, η προεξόφληση μερίσματος, η προεξόφληση σύνταξης, η μεταβίβαση μερίσματος καθώς και η αναθεώρηση μερίσματος.</span></p>', 1, 0, '1979-12-19 00:00:00', 5, 29, 0, NULL, 'greek', 'merismatoukhoi');
INSERT INTO `elx_sections` VALUES(11, 'Κρατήσεις', 'Κρατήσεις', NULL, 'content', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει πληροφορίες για τις κρατήσεις των μετόχων, των μη μετόχων, επί των δαπανών καθώς και η συναλλαγή 4300 μέσω Εθνικής τράπεζας της Ελλάδας (ΕΤΕ).</span></p>', 1, 0, '1979-12-19 00:00:00', 4, 29, 0, NULL, 'greek', 'krateseis');
INSERT INTO `elx_sections` VALUES(13, 'Οδηγός κρατήσεων', 'Οδηγός κρατήσεων', NULL, 'content', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει πληροφορίες αναλυτικά για τις κρατήσεις υπερ του ΜΤΠΥ.</span></p>', 1, 0, '1979-12-19 00:00:00', 2, 29, 0, NULL, 'greek', 'odegos-krateseon');
INSERT INTO `elx_sections` VALUES(12, 'Ελέγχοι', 'Ελέγχοι', NULL, 'content', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει πληροφορίες αναλυτικά σχετικά με το τμήμα ελεγκτών του ΜΤΠΥ. Πιο συγκεκριμένα περιγράφονται οι στόχοι, η δράση, οι ελεγχόμενες υπηρεσίες καθώς και οι επιτόπιοι ελέγχοι που διενεργήθηκαν.</span></p>', 1, 0, '1979-12-19 00:00:00', 3, 29, 0, NULL, 'greek', 'elegkhoi');
INSERT INTO `elx_sections` VALUES(14, 'Προς κοινό ΜΤΠΥ', 'Προς κοινό ΜΤΠΥ', 'politis.jpg', 'content', 'left', '<p><span style="font-family: arial;">Η πύλη αυτή περιλαμβάνει σημαντικές πληροφορίες για την γνώμη του κοινού είτε είναι μέλη του ΜΤΠΥ είτε συναλλασσόμενοι, τις οποίες θα λάβει το ΔΣ του Μετοχικού Ταμείου Πολιτικών Υπαλλήλων σοβαρά υπόψη του ώστε να βελτιώσει την λειτουργία και τις υπηρεσίες του προς το κοινό στο άμεσο μέλλον.</span></p>', 1, 0, '1979-12-19 00:00:00', 1, 29, 0, NULL, 'greek', 'pros-koino-mtpu');
