INSERT INTO `elx_weblinks` VALUES(11, 52, 0, 'Εθνικό Κέντρο Δημόσιας Διοίκησης', 'http://www.ekdd.gr/ekdda/index.php/gr/', 'Το Εθνικό Κέντρο Δημόσιας Διοίκησης και Αυτοδιοίκησης (ΕΚΔΔΑ) είναι ο εθνικός στρατηγικός φορέας ανάπτυξης του ανθρώπινου δυναμικού της δημόσιας διοίκησης και της τοπικής αυτοδιοίκησης. Ιδρύθηκε το 1983, είναι Νομικό Πρόσωπο Δημοσίου Δικαίου (ΝΠΔΔ) και υπ', '2014-02-05 07:47:44', 1, 1, 0, '1979-12-19 00:00:00', 1, 0, 1, 'target=1', 'inep.JPG', 'greek');
INSERT INTO `elx_weblinks` VALUES(12, 52, 0, 'Πανεπιστήμιο Αθηνών (e-learning)', 'http://elearn.elke.uoa.gr/elearn/apply.html', 'Το Πρόγραμμα εξ Αποστάσεως Εκπαίδευσης εντάχθηκε στο πλαίσιο της εκπαιδευτικής προσπάθειας που ξεκίνησε το Κέντρο Συνεχιζόμενης Εκπαίδευσης και Επιμόρφωσης του Πανεπιστημίου Αθηνών τον Οκτώβριο του 2001, σε συνέχεια της υλοποίησης των προγραμμάτων του Υπο', '2014-02-05 07:44:40', 0, 1, 0, '1979-12-19 00:00:00', 2, 0, 1, 'target=1', 'uoa.JPG', 'greek');
INSERT INTO `elx_weblinks` VALUES(13, 54, 0, 'ΟΣΥΟ', 'http://www.osyo.ondsl.gr/', 'Ομοσπονδία Συλλόγων Υπουργείου Οικονομικών', '2014-01-27 08:28:28', 0, 1, 0, '1979-12-19 00:00:00', 1, 0, 1, 'target=1', 'osyo.JPG', 'greek');
